(12, 1, 0, 1, 1, 4, '2009-07-01 09:11:39', '2009-08-05 12:46:18', '<a href="http://vimeo.com/1014998" class="exitlink">Video tutorial on Vimeo</a>\r\n<p>Source code:</p>\r\n<pre>\r\n#import "TableViewTestAppDelegate.h"\r\n\r\n@implementation TableViewTestAppDelegate\r\n\r\n@synthesize window;\r\n\r\n\r\n- (void)applicationDidFinishLaunching:(UIA pplication *)application {\r\ncontentArray = [[NSArray arrayWithObjects:@"First", @"Second", @"Third", nil] retain];\r\n[window addSubview:[mainNavController view]];\r\n}\r\n\r\n- (NSInteger)tableView:(UITableView *)tableView\r\nnumberOfRowsInSection:(NSInteger)section\r\n{\r\nreturn [contentArray count];\r\n}\r\n\r\n- (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath\r\n{\r\nstatic NSString *identity = @"MainCell";\r\nUITableViewCell *cell = [tableView dequeueReusableCellWithIdentifier:identi ty];\r\nif(cell == nil)\r\n{\r\ncell = [[[UITableViewCell alloc] initWithFrame:CGRectZero reuseIdentifier:identity] autorelease];\r\n}\r\ncell.text = [contentArray objectAtIndex:indexPath.row];\r\nreturn cell;\r\n}\r\n\r\n- (void)dealloc {\r\n[window release];\r\n[super dealloc];\r\n}\r\n\r\n@end\r\n</pre>', 'UITableView iPhone Programming Tutorial', 'uitableview-iphone-programming-tutorial', 0, '<p>A tutorial that teaches iPhone programmers how to populate a <strong>UITableView</strong> by Anthony Frizalone</p>', 1, '', '', '2009-08-05 12:46:18', '2009-08-05 12:46:18', 'UITableView iPhone Programming Tutorial A tutorial that teaches iPhone programmers how to populate a UITableView by Anthony Frizalone Video tutorial on Vimeo Source code: #import "TableViewTestAppDelegate.h" @implementation TableViewTestAppDelegate @synthesize window; - (void)applicationDidFinishLaunching:(UIA pplication *)application { contentArray = [[NSArray arrayWithObjects:@"First", @"Second", @"Third", nil] retain]; [window addSubview:[mainNavController view]]; } - (NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section { return [contentArray count]; } - (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath { static NSString *identity = @"MainCell"; UITableViewCell *cell = [tableView dequeueReusableCellWithIdentifier:identi ty]; if(cell == nil) { cell = [[[UITableViewCell alloc] initWithFrame:CGRectZero reuseIdentifier:identity] autorelease]; } cell.text = [contentArray objectAtIndex:indexPath.row]; return cell; } - (void)dealloc { [window release]; [super dealloc]; } @end', 0, 0, 0, 0, 0, 0),
(13, 1, 0, 1, 1, 1, '2009-07-01 09:16:07', '2009-07-02 21:30:42', '<object width="650" height="490"><param name="allowfullscreen" value="true" /><param name="allowscriptaccess" value="always" /><param name="movie" value="http://vimeo.com/moogaloop.swf?clip_id=817833&server=vimeo.com&show_title=1&show_byline=1&show_portrait=0&color=00ADEF&fullscreen=1" /><embed src="http://vimeo.com/moogaloop.swf?clip_id=817833&server=vimeo.com&show_title=1&show_byline=1&show_portrait=0&color=00ADEF&fullscreen=1" type="application/x-shockwave-flash" allowfullscreen="true" allowscriptaccess="always" width="650" height="490"></embed></object>\r\n<p>&nbsp;</p><p>&nbsp;</p>\r\n<p>Source code:</p>\r\n\r\n<pre>\r\nimport "MyView.h"\r\n\r\n#define TEXT_LABEL_FONT_SIZE 30.0\r\n#define TEXT_FIELD_HEIGHT_MULTIPLIER 2.0\r\n\r\n@implementation MyView\r\n\r\n- (id)initWithFrame:(CGRect)aRect\r\n{\r\n[super initWithFrame:aRect];\r\nself.backgroundColor = [UIColor whiteColor];\r\n\r\nCGRect contentFrame = self.frame;\r\nCGFloat leftMargin = 90.0;\r\nCGFloat textFieldWidth = contentFrame.size.width * 0.72;\r\nCGFloat textFieldOriginX = (contentFrame.size.width - textFieldWidth) / 2.0;\r\n\r\nCGRect labelFrame = CGRectMake(textFieldOriginX, leftMargin, textFieldWidth, TEXT_LABEL_FONT_SIZE * TEXT_FIELD_HEIGHT_MULTIPLIER);\r\nUILabel *someText = [[UILabel alloc] initWithFrame:labelFrame];\r\nsomeText.text = @"Hello World!";\r\nsomeText.font = [UIFont systemFontOfSize:TEXT_LABEL_FONT_SIZE];\r\nsomeText.textColor = [UIColor redColor];\r\nsomeText.textAlignment = UITextAlignmentCenter;\r\n\r\n[self addSubview:someText];\r\n[someText release];\r\n\r\nreturn self;\r\n} \r\n</pre>', 'Hello World iPhone Tutorial', 'hello-world-iphone-tutorial', 0, '<p>A screencast tutorial to help beginner iPhone programmers get started. This tutorial shows how to draw "Hello World!" text on the screen. </p>', 1, '', '', '2009-07-02 21:30:42', '2009-07-02 21:30:42', 'Hello World iPhone Tutorial A screencast tutorial to help beginner iPhone programmers get started. This tutorial shows how to draw "Hello World!" text on the screen.   &nbsp;&nbsp; Source code:   import "MyView.h"  #define TEXT_LABEL_FONT_SIZE 30.0 #define TEXT_FIELD_HEIGHT_MULTIPLIER 2.0  @implementation MyView  - (id)initWithFrame:(CGRect)aRect { [super initWithFrame:aRect]; self.backgroundColor = [UIColor whiteColor];  CGRect contentFrame = self.frame; CGFloat leftMargin = 90.0; CGFloat textFieldWidth = contentFrame.size.width * 0.72; CGFloat textFieldOriginX = (contentFrame.size.width - textFieldWidth) / 2.0;  CGRect labelFrame = CGRectMake(textFieldOriginX, leftMargin, textFieldWidth, TEXT_LABEL_FONT_SIZE * TEXT_FIELD_HEIGHT_MULTIPLIER); UILabel *someText = [[UILabel alloc] initWithFrame:labelFrame]; someText.text = @"Hello World!"; someText.font = [UIFont systemFontOfSize:TEXT_LABEL_FONT_SIZE]; someText.textColor = [UIColor redColor]; someText.textAlignment = UITextAlignmentCenter;  [self addSubview:someText]; [someText release];  return self; }', 0, 0, 0, 0, 0, 0),
