eginAnimations:nil context:NULL]; [UIView setAnimationDuration: 0.2]; if (switch3.on) resSwitch.on = YES; else resSwitch.on = NO; [UIView commitAnimations]; } // ------ Tutorial code ends here ------ /* // The designated initializer. Override to perform setup that is required before the view is loaded. - (id)initWithNibName:(NSString *)nibNameOrNil bundle:(NSBundle *)nibBundleOrNil { if (self = [super initWithNibName:nibNameOrNil bundle:nibBundleOrNil]) { // Custom initialization } return self; } */ /* // Implement loadView to create a view hierarchy programmatically, without using a nib. - (void)loadView { } */ /* // Implement viewDidLoad to do additional setup after loading the view, typically from a nib. - (void)viewDidLoad { [super viewDidLoad]; } */ /* // Override to allow orientations other than the default portrait orientation. - (BOOL)shouldAutorotateToInterfaceOrientation:(UIInterfaceOrientation)interfaceOrientation { // Return YES for supported orientations return (interfaceOrientation == UIInterfaceOrientationPortrait); } */ - (void)didReceiveMemoryWarning { // Releases the view if it doesn''t have a superview. [super didReceiveMemoryWarning]; // Release any cached data, images, etc that aren''t in use. } - (void)viewDidUnload { // Release any retained subviews of the main view. // e.g. self.myOutlet = nil; } // This function is for button which takes you to the xprogress.com website - (IBAction) runXprogressComButton: (id) sender { NSURL *url = [ [ NSURL alloc ] initWithString: @"http://www.xprogress.com/" ]; [[UIApplication sharedApplication] openURL:url]; } - (void)dealloc { // ------ Tutorial code starts here ------ [switch1 release]; [switch2 release]; [switch3 release]; [resLabel release]; [resSwitch release]; [resTextField release]; // ------ Tutorial code ends here ------ [super dealloc]; } @end In the first part of the code we need to synthesize all the variables (This will automatically create all the functionality you''ll need, like set or get the data from the variable) @synthesize switch1; @synthesize switch2; @synthesize switch3; @synthesize resLabel; @synthesize resSwitch; @synthesize resTextField; Than we need to create all the actual functions which are going to handle action after the stats of the UISwitch will change: - (IBAction) toggleEnabledTextForSwitch1onSomeLabel: (id) sender { if (switch1.on) resLabel.text = @"Enabled"; else resLabel.text = @"Disabled"; } - (IBAction) toggleEnabledTextFieldForSwitch2: (id) sender { if (switch2.on) resTextField.enabled = YES; else { resTextField.enabled = NO; resTextField.text = @"Disabled field"; } } - (IBAction) toggleOnForSwitch3: (id) sender { [UIView beginAnimations:nil context:NULL]; [UIView setAnimationDuration: 0.3]; if (switch3.on) resSwitch.on = YES; else resSwitch.on = NO; [UIView commitAnimations]; } First function, toggleEnabledTextForSwitch1onSomeLabel, is setting text of your label to Enabled if the switch one is ON and disabled if the switch is OFF Second function, toggleEnabledTextFieldForSwitch2, is actually enabling and disabling the text field and additionally, once the field is disabled, is setting the text property of the field to "Disabled field" Third function toggleOnForSwitch3 is the most complicated one. This function is setting the other switch to the same position as is the first one. All the code which is closed into square brackets is handling how smoothly is the responding switch changing it''s state. You can try to disable this functionality or change the delay for the animation like that: - (IBAction) toggleOnForSwitch3: (id) sender { //[UIView beginAnimations:nil context:NULL]; //[UIView setAnimationDuration: 0.2]; // this will just switch the second switch without any animation if (switch3.on) resSwitch.on = YES; else resSwitch.on = NO; //[UIView commitAnimations]; } or - (IBAction) toggleOnForSwitch3: (id) sender { [UIView beginAnimations:nil context:NULL]; // Now I am extending the animation time [UIView setAnimationDuration: 0.8]; if (switch3.on) resSwitch.on = YES; else resSwitch.on = NO; [UIView commitAnimations]; } The coding part is now done and we can start connecting the code to the interface in Interface Builder Open the TutorialProjectViewController.xib file and than the first thing we need to is to connect all the elements to their variables. Press Ctrl key and hold it, drag the "File''s owner icon from the list. This will create nice blue line connecting File''s owner and your pointed, than release the mouse over the switch one (drag and drop while holding Ctrl) and you''ll see menu with all the variables with UISwitch type we''ve declared. In this case select switch1 Once the switch1 is connected with it''s component, connect switch2 and switch3 Now when you have these connected, I can tell you that we are not using these variables to control anything, but I was expecting you messing around the app, once we will be done and those connections may become quite handy Now please dothe same connections with "res" variables (resLabel, resTextField, resSwitch) following these pictures: Now we need to connect our switches to the functions we''ve created. Now we have two options how to do that, first one is to hold the Ctrl key again and than drag the switch you want to assign the function to and drop it over the "File''s owner" icon and than select the function you want to use from the small pop-up menu OR as you can see on those following pictures right click on the switch and drag the small circle on the right side of the line with "Value changed" and than drop it over the "File''s owner" icon and the rest is the same, you just need to pick the correct function. Here you can see how the connecting works. Select the toggleEnabledTextForSwitch1onSomeLabel function for "Label switch" Select the toggleEnabledTextFieldForSwitch2 function for "TextField switch" Select the toggleOnForSwitch3 function for "Switch switch" :) Only remaining thing we need to do is cross fingers and launch the application :) It should look like that: I hope that you''ve enjoyed this tutorial and that I helped you a little bit on your "Path of the iPhone developer". And I hope tat you will come back soon to check other new tutorials .... I you found any grammatical mistake I''ve made please tell me, I am not a native speaker than I will really appreciate any comment from your side. See u soon, Ondrej', 0, 0, 0, 0, 0, 1),
(31, 1, 0, 1, 3, 0, '2009-08-12 19:02:44', '2009-08-12 19:28:15', '<p>I''ve build this website recently, just for fun because I had this PHP library I was using for a different project for a while and I thought that this tool might be quite useful.</p>\r\n\r\n<p>Website has a something like a webservice, but it''s actually just a script which is generating unique id of the link ... request like that <em>http://ffsurl.com/api/create-url/?url=http://www.example.com/</em> will return just number <em>62</em>.</p>\r\n\r\n<p>The URL you are passing trough needs to be encoded of course ... in JavaScript I''ve used the <em>url = encodeURIComponent(url);</em></p>\r\n\r\n<p><a href="http://ffsurl.com/" class="exitlink">http://ffsurl.com/</a></p>\r\n\r\n<p>The current source code (<em>main.js from the dashcode project</em>) is below. And I''ve imported jQuery to do all the important stuff.</p>\r\n\r\n<pre>\r\n\r\nfunction getIdCode(event)\r\n{\r\n    var url = document.getElementById(''myUrl'').value;\r\n    url = encodeURIComponent(url);\r\n    $(''#sUrl'').val(url);\r\n    $.ajax({\r\n		type: ''POST'',\r\n		url: ''http://ffsurl.com/admin/system/tinyurl/ajax/createUrl.php'',\r\n		data: ''url='' + $(''#sUrl'').val(),\r\n		success: function(id){\r\n			$(''#newUrl'').val(''http://ffsurl.com/u'' + id + ''/'');\r\n            $(''#newUrl'').attr(''style'', ''color: black;'');\r\n			if (id == 0) {\r\n				setMessage(''Please enter valid URL'');\r\n			}\r\n		},\r\n		error: function(xreq, stErr, err) {\r\n			setMessage( ''Data not saved, error: '' + xreq.status + '', '' + xreq.statusText );\r\n		}\r\n	});\r\n}\r\n\r\nfunction setMessage(text) {\r\n    $(''#newUrl'').val(text);\r\n    $(''#newUrl'').attr(''style'', ''color: red;'');\r\n}\r\n\r\nfunction myGoToFFS(event)\r\n{\r\n    // Insert Code Here\r\n}\r\n\r\n\r\nfunction myGoToFfsUrl(event)\r\n{\r\n    // Insert Code Here\r\n}\r\n\r\n</pre>\r\n\r\n<p>Maybe you''ll find useful that if you want to access anything on the network from the DashCode you need to allow network access in the <em>Info.plist</em> file. You can do it by adding <em>&lt;key&gt;AllowNetworkAccess&lt;/key&gt;</em>  with value <em>&lt;true/&gt;</em>. Info.plist file will look something like that:</p>\r\n<pre>\r\n&lt;?xml version="1.0" encoding="UTF-8"?>\r\n&lt;!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd">\r\n&lt;plist version="1.0">\r\n&lt;dict>\r\n	<key>CFBundleDevelopmentRegion</key>\r\n	<string>English</string>\r\n	<key>CFBundleDisplayName</key>\r\n	<string>ffsUrl</string>\r\n	<key>CFBundleIdentifier</key>\r\n	<string>com.ondrej.widget.ffsUrl</string>\r\n	<key>CFBundleName</key>\r\n	<string>ffsUrl</string>\r\n	<key>CFBundleShortVersionString</key>\r\n	<string>1.0</string>\r\n	<key>CFBundleVersion</key>\r\n	<string>1.0</string>\r\n	<key>CloseBoxInsetX</key>\r\n	<integer>15</integer>\r\n	<key>CloseBoxInsetY</key>\r\n	<integer>15</integer>\r\n	<key>Height</key>\r\n	<integer>182</integer>\r\n	<key>MainHTML</key>\r\n	<string>main.html</string>\r\n	<key>Width</key>\r\n	<integer>342</integer>\r\n\r\n	&lt;!-- here it starts --&gt;\r\n	<key>AllowNetworkAccess</key>\r\n	&lt;true />\r\n	&lt;!-- here it ends --&gt;\r\n\r\n&lt;/dict>\r\n&lt;/plist>\r\n\r\n</pre>\r\n\r\n<p>Just download the installer in the top right corner. I''ll be happy to hear some feedback from you :-)</p>', 'ffsurl.com - URL shortener (something like TinyUrl)', 'ffsurl-com-url-shortener-something-like-tinyurl', 0, '<p>Check this app out, it''s quite easy to develop anything in DashCode on a Mac because it''s a pure HTML and JavaScript</p>\r\n', 1, '', '', '2009-08-12 19:28:15', '2009-08-12 19:28:15', 'ffsurl.com - URL shortener (something like TinyUrl) Check this app out, it''s quite easy to develop anything in DashCode on a Mac because it''s a pure HTML and JavaScript I''ve build this website recently, just for fun because I had this PHP library I was using for a different project for a while and I thought that this tool might be quite useful. Website has a something like a webservice, but it''s actually just a script which is generating unique id of the link ... request like that http://ffsurl.com/api/create-url/?url=http://www.example.com/ will return just number 62. The URL you are passing trough needs to be encoded of course ... in JavaScript I''ve used the url = encodeURIComponent(url); http://ffsurl.com/ The current source code (main.js from the dashcode project) is below. And I''ve imported jQuery to do all the important stuff. function getIdCode(event) { var url = document.getElementById(''myUrl'').value; url = encodeURIComponent(url); $(''#sUrl'').val(url); $.ajax({ type: ''POST'', url: ''http://ffsurl.com/admin/system/tinyurl/ajax/createUrl.php'', data: ''url='' + $(''#sUrl'').val(), success: function(id){ $(''#newUrl'').val(''http://ffsurl.com/u'' + id + ''/''); $(''#newUrl'').attr(''style'', ''color: black;''); if (id == 0) { setMessage(''Please enter valid URL''); } }, error: function(xreq, stErr, err) { setMessage( ''Data not saved, error: '' + xreq.status + '', '' + xreq.statusText ); } }); } function setMessage(text) { $(''#newUrl'').val(text); $(''#newUrl'').attr(''style'', ''color: red;''); } function myGoToFFS(event) { // Insert Code Here } function myGoToFfsUrl(event) { // Insert Code Here } Maybe you''ll find useful that if you want to access anything on the network from the DashCode you need to allow network access in the Info.plist file. You can do it by adding &lt;key&gt;AllowNetworkAccess&lt;/key&gt; with value &lt;true/&gt;. Info.plist file will look something like that: &lt;?xml version="1.0" encoding="UTF-8"?> &lt;!DOCTYPE plist PUBLIC "-//Apple//DTD PLIST 1.0//EN" "http://www.apple.com/DTDs/PropertyList-1.0.dtd"> &lt;plist version="1.0"> &lt;dict> CFBundleDevelopmentRegion English CFBundleDisplayName ffsUrl CFBundleIdentifier com.ondrej.widget.ffsUrl CFBundleName ffsUrl CFBundleShortVersionString 1.0 CFBundleVersion 1.0 CloseBoxInsetX 15 CloseBoxInsetY 15 Height 182 MainHTML main.html Width 342 &lt;!-- here it starts --&gt; AllowNetworkAccess &lt;true /> &lt;!-- here it ends --&gt; &lt;/dict> &lt;/plist> Just download the installer in the top right corner. I''ll be happy to hear some feedback from you :-)', 0, 0, 0, 0, 0, 0);
