(51, 1, 0, 1, 1, 0, '2010-02-23 15:34:21', '2010-02-24 17:23:52', '<p>Ok, lets start with the functions that are available in the library. Two files that are included in the header file are included in the archive, which can be downloaded in the top right corner of this tutorial. I will describe what each function is doing later.</p>\r\n\r\n<pre>\r\n//\r\n//  wgFileDb.h\r\n//  iBBClone\r\n//\r\n//  Created by Ondrej Rafaj on 21.2.10.\r\n//  Copyright 2010 Home. All rights reserved.\r\n//\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "wgIo.h"\r\n#import "wgPaths.h"\r\n\r\n\r\n\r\n\r\n#define kFileDbFolder		@"wgFileDb"\r\n#define kFileDbAIKey		@"autoincrement"\r\n#define kFileDbDataKey		@"data"\r\n#define kFileDbIndexKey		@"index"\r\n#define kMainIdKey			@"wgFileDbSecretId"\r\n\r\n\r\n\r\n@interface wgFileDb : NSObject {\r\n\r\n}\r\n\r\n+ (int)getId:(NSDictionary *)item;\r\n\r\n+ (NSString *)getPathToDb:(NSString *)dbName;\r\n\r\n+ (BOOL)isDatabase:(NSString *)dbName;\r\n\r\n+ (BOOL)deleteDb:(NSString *)dbName;\r\n\r\n+ (BOOL)truncateDb:(NSString *)dbName;\r\n\r\n+ (int)getAutoincrementNumberForDb:(NSString *)dbName;\r\n\r\n+ (NSArray *)getItemsFromDb:(NSString *)dbName;\r\n\r\n+ (void)saveFullData:(NSArray *)arr toDb:(NSString *)dbName;\r\n\r\n+ (int)getNumberOfItemsInDb:(NSString *)dbName;\r\n\r\n+ (NSDictionary *)getItem:(int)idItem inDb:(NSString *)dbName;\r\n\r\n+ (int)addItemToBottom:(NSDictionary *)item intoDb:(NSString *)dbName;\r\n\r\n+ (int)addItemToTop:(NSDictionary *)item intoDb:(NSString *)dbName;\r\n\r\n+ (void)deleteItem:(int)idItem fromDb:(NSString *)dbName;\r\n\r\n+ (void)updateItem:(int)idItem withData:(NSDictionary *)item inDb:(NSString *)dbName;\r\n\r\n+ (void)moveTableItem:(NSIndexPath *)fromIndexPath to:(NSIndexPath *)toIndexPath inDb:(NSString *)dbName;\r\n\r\n+ (int)duplicateItemToBottom:(int)idItem inDb:(NSString *)dbName;\r\n\r\n+ (int)duplicateItemToTop:(int)idItem inDb:(NSString *)dbName;\r\n\r\n@end\r\n</pre>\r\n\r\n<p>As you can see from the list, there is quite few methods that are giving you an opportunity to manage almost any dataset. The only thing I want to add is sorting.</p>\r\n\r\n<p>Here you can see the .m file with all the comments that are relevant to each function.</p>\r\n\r\n<pre>\r\n//\r\n//  wgFileDb.m\r\n//  iBBClone\r\n//\r\n//  Created by Ondrej Rafaj on 21.2.10.\r\n//  Copyright 2010 Home. All rights reserved.\r\n//\r\n\r\n#import "wgFileDb.h"\r\n\r\n@implementation wgFileDb\r\n\r\n// public; returns id from given object\r\n+ (int)getId:(NSDictionary *)item {\r\n	return [[item objectForKey:kMainIdKey] intValue];\r\n}\r\n\r\n// private; converts number to number in the string\r\n+ (NSString *)_encodeNumber:(int)num {\r\n	return [NSString stringWithFormat:@"%d", num];\r\n}\r\n\r\n// private; returns path to the documents/db folder\r\n+ (NSString *)_getPathToDb:(NSString *)dbName {\r\n	NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths databaseDirectory], kFileDbFolder];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return [NSString stringWithFormat:@"%@%@", p, dbName];\r\n}\r\n\r\n// public; returns path to the documents/db folder, creates the path if this one doesn''t exist and creates empty db file\r\n+ (NSString *)getPathToDb:(NSString *)dbName {\r\n	NSString *p = [self _getPathToDb:dbName];\r\n	if (![wgIo isFile:p]) {\r\n		NSMutableDictionary *d = [[[NSMutableDictionary alloc] init] autorelease];\r\n		[d setObject:[self _encodeNumber:0] forKey:kFileDbAIKey];\r\n		[d setObject:[[[NSMutableArray alloc] init] autorelease] forKey:kFileDbDataKey];\r\n		[d setObject:[[[NSMutableDictionary alloc] init] autorelease] forKey:kFileDbIndexKey];\r\n		[d writeToFile:p atomically:YES];\r\n		if (![wgIo isFile:p]) NSAssert(@"Unable to create database ''%@'' in wgFileDb.", dbName);\r\n	}\r\n	return p;\r\n}\r\n\r\n// public; checks if database file exists\r\n+ (BOOL)isDatabase:(NSString *)dbName {\r\n	NSString *p = [self _getPathToDb:dbName];\r\n	return [wgIo isFile:p];\r\n}\r\n\r\n// private; returns full database file\r\n+ (NSDictionary *)_getFullDataForDb:(NSString *)dbName {\r\n	NSString *p = [self getPathToDb:dbName];\r\n	return [[[NSDictionary alloc] initWithContentsOfFile:p] autorelease];\r\n}\r\n\r\n// public; deletes the database file\r\n+ (BOOL)deleteDb:(NSString *)dbName {\r\n	NSString *p = [self getPathToDb:dbName];\r\n	if ([wgIo isFile:p]) return [wgIo deleteFile:p];\r\n	else return YES;\r\n}\r\n\r\n// public; truncates the databse fil and sets autoincrement value to zero\r\n+ (BOOL)truncateDb:(NSString *)dbName {\r\n	[self deleteDb:dbName];\r\n	NSString *p = [self getPathToDb:dbName];\r\n	return [wgIo isFile:p];\r\n}\r\n\r\n// public; returns value of actual autoincrement number\r\n+ (int)getAutoincrementNumberForDb:(NSString *)dbName {\r\n	NSDictionary *d = [self _getFullDataForDb:dbName];\r\n	return (int) [[d objectForKey:kFileDbAIKey] intValue];\r\n}\r\n\r\n// public; returns all items from the db as NSArray\r\n+ (NSArray *)getItemsFromDb:(NSString *)dbName {\r\n	NSDictionary *d = [self _getFullDataForDb:dbName];\r\n	return (NSArray *) [d objectForKey:kFileDbDataKey];\r\n}\r\n\r\n// private; converts id of the item to index position in the array\r\n+ (int)_getIndexForId:(int)idItem inDb:(NSString *)dbName {\r\n	NSDictionary *d = [self _getFullDataForDb:dbName];\r\n	return (int) [[[d objectForKey:kFileDbIndexKey] objectForKey:[self _encodeNumber:idItem]] intValue];\r\n}\r\n\r\n// private; recreates index for all the id''s and items in the db\r\n+ (void)_reloadIndexTableInDb:(NSString *)dbName {\r\n	NSMutableDictionary *f = (NSMutableDictionary *)[self _getFullDataForDb:dbName];\r\n	NSArray *arr = (NSArray *)[f objectForKey:kFileDbDataKey];\r\n	NSMutableDictionary *n = [[[NSMutableDictionary alloc] init] autorelease];\r\n	int index = 0;\r\n	for (NSDictionary *d in arr) {\r\n		[n setObject:[self _encodeNumber:index] forKey:[d objectForKey:kMainIdKey]];\r\n		index++;\r\n	}\r\n	[f setObject:n forKey:kFileDbIndexKey];\r\n	NSString *p = [self getPathToDb:dbName];\r\n	[f writeToFile:p atomically:YES];\r\n}\r\n\r\n// private; increases autoincrement value for the databse\r\n+ (int)_increaseAIinDb:(NSString *)dbName {\r\n	NSMutableDictionary *f = (NSMutableDictionary *)[self _getFullDataForDb:dbName];\r\n	int ai = ([self getAutoincrementNumberForDb:dbName] + 1);\r\n	[f setObject:[self _encodeNumber:ai] forKey:kFileDbAIKey];\r\n	NSString *p = [self getPathToDb:dbName];\r\n	[f writeToFile:p atomically:YES];\r\n	return ai;\r\n}\r\n\r\n// public, saves the full given array back to he database\r\n+ (void)saveFullData:(NSArray *)arr toDb:(NSString *)dbName {\r\n	NSMutableDictionary *f = (NSMutableDictionary *)[self _getFullDataForDb:dbName];\r\n	[f setObject:arr forKey:kFileDbDataKey];\r\n	NSString *p = [self getPathToDb:dbName];\r\n	[f writeToFile:p atomically:YES];\r\n	[self _reloadIndexTableInDb:dbName];\r\n}\r\n\r\n// public; returns number of items in the selected db\r\n+ (int)getNumberOfItemsInDb:(NSString *)dbName {\r\n	NSArray *arr = [self getItemsFromDb:dbName];\r\n	return [arr count];\r\n}\r\n\r\n// public; returns one item with specific id\r\n+ (NSDictionary *)getItem:(int)idItem inDb:(NSString *)dbName {\r\n	NSArray *arr = [self getItemsFromDb:dbName];\r\n	return (NSDictionary *)[arr objectAtIndex:[self _getIndexForId:idItem inDb:dbName]];\r\n}\r\n\r\n// public; add given item to the end of the database\r\n+ (int)addItemToBottom:(NSDictionary *)item intoDb:(NSString *)dbName {\r\n	NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName];\r\n	int i = [self _increaseAIinDb:dbName];\r\n	NSMutableDictionary *nd = (NSMutableDictionary *)item;\r\n	[nd setObject:[self _encodeNumber:i] forKey:kMainIdKey];\r\n	[arr addObject:nd];\r\n	[self saveFullData:arr toDb:dbName];\r\n	return i;\r\n}\r\n\r\n// public; add given item to the beginning of the database\r\n+ (int)addItemToTop:(NSDictionary *)item intoDb:(NSString *)dbName {\r\n	NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName];\r\n	NSMutableArray *narr = [[[NSMutableArray alloc] init] autorelease];\r\n	int i = [self _increaseAIinDb:dbName];\r\n	NSMutableDictionary *nd = (NSMutableDictionary *)item;\r\n	[nd setObject:[self _encodeNumber:i] forKey:kMainIdKey];\r\n	[narr addObject:nd];\r\n	for (NSDictionary *d in arr) [narr addObject:d];\r\n	[self saveFullData:narr toDb:dbName];\r\n	return i;\r\n}\r\n\r\n// public; deletes id specific item from the database\r\n+ (void)deleteItem:(int)idItem fromDb:(NSString *)dbName {\r\n	NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName];\r\n	[arr removeObjectAtIndex:[self _getIndexForId:idItem inDb:dbName]];\r\n	[self saveFullData:arr toDb:dbName];\r\n}\r\n\r\n// public; updates id specific item in the database\r\n+ (void)updateItem:(int)idItem withData:(NSDictionary *)item inDb:(NSString *)dbName {\r\n	NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName];\r\n	NSMutableArray *narr = [[[NSMutableArray alloc] init] autorelease];\r\n	int ai;\r\n	for (NSDictionary *d in arr) {\r\n		ai = [[d objectForKey:kMainIdKey] intValue];\r\n		if (ai == idItem) [narr addObject:item];\r\n		else [narr addObject:d];\r\n	}\r\n	[self saveFullData:narr toDb:dbName];\r\n}\r\n\r\n// public; moves item in the db, the input values are the same that are coming from:\r\n// - (void)tableView:(UITableView *)tableView moveRowAtIndexPath:(NSIndexPath *)fromIndexPath toIndexPath:(NSIndexPath *)toIndexPath\r\n+ (void)moveTableItem:(NSIndexPath *)fromIndexPath to:(NSIndexPath *)toIndexPath inDb:(NSString *)dbName {\r\n	NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName];\r\n	NSDictionary *i = [[arr objectAtIndex:fromIndexPath.row] retain];\r\n	[arr removeObjectAtIndex:fromIndexPath.row];\r\n	[arr insertObject:i atIndex:toIndexPath.row];\r\n	[self saveFullData:arr toDb:dbName];\r\n}\r\n\r\n// public; duplicates id specific item in the database and adds this one to the end of db\r\n+ (int)duplicateItemToBottom:(int)idItem inDb:(NSString *)dbName {\r\n	NSMutableDictionary *d = (NSMutableDictionary *)[self getItem:idItem inDb:dbName];\r\n	[d removeObjectForKey:kMainIdKey];\r\n	return [self addItemToBottom:d intoDb:dbName];\r\n}\r\n\r\n// public; duplicates id specific item in the database and adds this one to the beginning of db\r\n+ (int)duplicateItemToTop:(int)idItem inDb:(NSString *)dbName {\r\n	NSMutableDictionary *d = (NSMutableDictionary *)[self getItem:idItem inDb:dbName];\r\n	[d removeObjectForKey:kMainIdKey];\r\n	return [self addItemToTop:d intoDb:dbName];\r\n}\r\n\r\n// method in development, comments welcome\r\n+ (NSArray *)sortAscendingByKey:(NSString *)key inDb:(NSString *)dbName {\r\n	return [self getItemsFromDb:dbName];\r\n}\r\n\r\n// method in development, comments welcome\r\n+ (NSArray *)sortDescendingByKey:(NSString *)key inDb:(NSString *)dbName {\r\n	return [self getItemsFromDb:dbName];\r\n}\r\n\r\n@end\r\n</pre>\r\n\r\n<p>In the end, there are the file handling libraries Im using to unify paths and check for existing files.</p>\r\n\r\n<p>File handling library:</p>\r\n\r\n<pre>\r\n//\r\n//  wgIo.h\r\n//  iDeviant\r\n//\r\n//  Created by Ondrej Rafaj on 26.11.09.\r\n//  Copyright 2009 Home. All rights reserved.\r\n//\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "wgPaths.h"\r\n\r\n@interface wgIo : NSObject {\r\n\r\n}\r\n\r\n+ (NSString *)readFile:(NSString *)filePath;\r\n\r\n+ (NSDictionary *)getFileAttributes:(NSString *)path;\r\n\r\n+ (NSMutableArray *)getFilesArray:(NSString *)path;\r\n\r\n+ (int)getFileSize:(NSString *)path;\r\n\r\n+ (NSString *)getFileExtension:(NSString *)path;\r\n\r\n+ (NSString *)getFormatedFileSize:(NSString *)path;\r\n\r\n+ (NSString *)getFileCreated:(NSString *)path;\r\n\r\n+ (BOOL)createFolderIfNotExists:(NSString *)path;\r\n\r\n+ (BOOL)isDirectory:(NSString *)path;\r\n\r\n+ (BOOL)isFile:(NSString *)path;\r\n\r\n+ (BOOL)deleteFile:(NSString *)path;\r\n\r\n\r\n@end\r\n</pre>\r\n\r\n<p>Main library file:</p>\r\n\r\n<pre>\r\n//\r\n//  wgIo.m\r\n//  iDeviant\r\n//\r\n//  Created by Ondrej Rafaj on 26.11.09.\r\n//  Copyright 2009 Home. All rights reserved.\r\n//\r\n\r\n#import "wgIo.h"\r\n\r\n\r\n@implementation wgIo\r\n\r\n\r\n+ (NSString *)readFile:(NSString *)filePath {\r\n	NSError *error;\r\n	return [NSString stringWithContentsOfFile:filePath encoding:NSUTF8StringEncoding error:&error];\r\n}\r\n\r\n+ (BOOL)isDirectory:(NSString *)path {\r\n	BOOL isDir;\r\n	NSFileManager *fileManager = [NSFileManager defaultManager];\r\n	if ([fileManager fileExistsAtPath:path isDirectory:&isDir] && isDir) return YES;\r\n	else return NO;\r\n}\r\n\r\n+ (BOOL)isFile:(NSString *)path {\r\n	BOOL isDir;\r\n	NSFileManager *fileManager = [NSFileManager defaultManager];\r\n	if ([fileManager fileExistsAtPath:path isDirectory:&isDir]) {\r\n		if (isDir) return NO;\r\n		else return YES;\r\n	}\r\n	else return NO;\r\n}\r\n\r\n+ (BOOL)deleteFile:(NSString *)path {\r\n	NSFileManager *fileManager = [NSFileManager defaultManager];\r\n	return [fileManager removeItemAtPath:path error:nil];\r\n}\r\n\r\n\r\n+ (NSDictionary *)getFileAttributes:(NSString *)path {\r\n	NSFileManager *manager = [NSFileManager defaultManager];\r\n	NSDictionary *fileAttributes = [manager fileAttributesAtPath:path traverseLink:false];\r\n	return fileAttributes;\r\n}\r\n\r\n+ (int)getFileSize:(NSString *)path {\r\n	NSDictionary *fileAttributes = [self getFileAttributes:path];\r\n	int ret = [[fileAttributes objectForKey:NSFileSize] intValue];\r\n	return ret;\r\n}\r\n\r\n+ (NSString *)getFileExtension:(NSString *)path {\r\n	NSString *ret = [path pathExtension];\r\n	return ret;\r\n}\r\n\r\n+ (NSString *)getFileCreated:(NSString *)path {\r\n	NSDictionary *fileAttributes = [self getFileAttributes:path];\r\n	NSDate *fileCreationDate = [fileAttributes objectForKey:NSFileCreationDate];\r\n	NSString *fileCreationDateString = [fileCreationDate description];\r\n	return fileCreationDateString;\r\n}\r\n\r\n+ (NSString *)getFormatedFileSize:(NSString *)path {\r\n	int fileSize = [self getFileSize:path];\r\n	NSString *extension;\r\n	NSString *formatedFileSize;\r\n	float ff;\r\n	if (fileSize < 1024) {\r\n		formatedFileSize = [NSString stringWithFormat:@"%d", fileSize];\r\n		extension = @"bytes";\r\n	}\r\n	else if (fileSize < 1048576) {\r\n		ff = fileSize / 1024;\r\n		formatedFileSize = [NSString stringWithFormat:@"%.2f", ff];\r\n		extension = @"Kb";\r\n	}\r\n	else {\r\n		ff = fileSize / 1048576;\r\n		formatedFileSize = [NSString stringWithFormat:@"%.2f", ff];\r\n		extension = @"Mb";\r\n	}\r\n	return [NSString stringWithFormat:@"%@ %@", formatedFileSize, extension];\r\n}\r\n\r\n+ (NSMutableArray *)getFilesArray:(NSString *)path {\r\n	NSMutableArray *fileArray = [[[NSMutableArray alloc] init] autorelease];\r\n	NSFileManager *manager = [NSFileManager defaultManager];\r\n    NSArray *fileList = [manager directoryContentsAtPath:[wgPaths filesDirectory]];\r\n    for (NSString *file in fileList){\r\n        if (![file isEqualToString:@"~"]) [fileArray addObject:file];\r\n	}\r\n	return fileArray;\r\n}\r\n\r\n+ (BOOL)createFolderIfNotExists:(NSString *)path {\r\n	if (![self isDirectory:path]) {\r\n		NSFileManager *fileManager = [NSFileManager defaultManager];\r\n		//NSDictionary *dic = [NSDictionary dictionaryWithObjectsAndKeys: [NSDate date], NSFileModificationDate, @"owner", @"NSFileOwnerAccountName", @"group", @"NSFileGroupOwnerAccountName", nil, @"NSFilePosixPermissions", [NSNumber numberWithBool:YES], @"NSFileExtensionHidden", nil];\r\n		return [fileManager createDirectoryAtPath:path attributes:nil];\r\n	}\r\n	else return YES;\r\n}\r\n\r\n\r\n@end\r\n</pre>\r\n\r\n<p>And heres the library that handles paths.</p>\r\n\r\n<p>Header file:</p>\r\n\r\n<pre>\r\n//\r\n//  wgPaths.h\r\n//  iDeviant\r\n//\r\n//  Created by Ondrej Rafaj on 26.11.09.\r\n//  Copyright 2009 Home. All rights reserved.\r\n//\r\n\r\n#import <Foundation/Foundation.h>\r\n#import "wgIo.h"\r\n\r\n@interface wgPaths : NSObject {\r\n\r\n}\r\n\r\n+ (NSString *)tempDirectory;\r\n\r\n+ (NSString *)confDirectory;\r\n\r\n+ (NSString *)documentsDirectory;\r\n\r\n+ (NSString *)filesDirectory;\r\n\r\n+ (NSString *)webDirectory;\r\n\r\n+ (NSString *)databaseDirectory;\r\n\r\n+ (NSString *)systemDirectory;\r\n\r\n+ (NSString *)cacheDirectory;\r\n\r\n+ (NSString *)imageCacheDirectory;\r\n\r\n+ (NSString *)imageCacheDirectory:(NSString *)subFolder;\r\n\r\n+ (NSString *)sqlFile:(NSString *)databaseName;\r\n\r\n@end\r\n</pre>\r\n\r\n\r\n<p>And at last the main lib file:</p>\r\n\r\n<pre>\r\n//\r\n//  wgPaths.m\r\n//  iDeviant\r\n//\r\n//  Created by Ondrej Rafaj on 26.11.09.\r\n//  Copyright 2009 Home. All rights reserved.\r\n//\r\n\r\n#import "wgPaths.h"\r\n\r\n#define kSysFolderName				@"~"\r\n\r\n@implementation wgPaths\r\n\r\n+ (NSString *)documentsDirectory {\r\n    NSArray *paths = NSSearchPathForDirectoriesInDomains(NSDocumentDirectory, NSUserDomainMask, YES);\r\n	[wgIo createFolderIfNotExists:[NSString stringWithFormat:@"%@/%@/", [paths objectAtIndex:0], kSysFolderName]];\r\n	return [paths objectAtIndex:0];\r\n}\r\n\r\n+ (NSString *)confDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"config"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)filesDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@/%@/", [self documentsDirectory], @"files"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)tempDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"temp"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)systemDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"system"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)cacheDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths systemDirectory], @"cache"];\r\n	if (![wgIo createFolderIfNotExists:p]) NSLog(@"Error creating directory: %@", p);\r\n	return p;\r\n}\r\n\r\n+ (NSString *)imageCacheDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths cacheDirectory], @"imagecache"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)imageCacheDirectory:(NSString *)subFolder {\r\n	NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths imageCacheDirectory], subFolder];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)databaseDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"db"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)webDirectory {\r\n	NSString *p = [NSString stringWithFormat:@"%@/~/%@/", [self documentsDirectory], @"htdocs"];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n+ (NSString *)sqlFile:(NSString *)databaseName {\r\n	NSString *p = [NSString stringWithFormat:@"%@%@.sqlite3", [self databaseDirectory], databaseName];\r\n	[wgIo createFolderIfNotExists:p];\r\n	return p;\r\n}\r\n\r\n@end\r\n</pre>\r\n\r\n<p>Thanks for reading, some tutorial about how to use those libraries with full functionality of UITableView (add, delete, reorder and edit) will follow shortly than stay tuned. :)</p>\r\n\r\n<p>Have nice day,</p>\r\n\r\n<p><strong>Ondrej</strong></p>\r\n\r\n', 'Small database handling library optimised for UITableView', 'small-database-handling-library-optimised-for-uitableview', 0, '<p>Hey Kids, today I want to tell you about library I wrote. It works like a tiny little database (without search), it is reusable within the application as all entries are stored in their own file. The entire library is based on NSArray (table) and NSDictionary (row).</p>\r\n\r\n<p>This library should cover all the main functions everyone needs when working with tables, including easy reordering system. This library is not designed for large amount of data, but up to 100 items should be fine. Sorry for calling this thing a db but Ive had no better idea ...</p>', 0, '', '', '2010-02-24 17:23:52', '2010-02-24 17:23:52', 'Small database handling library optimised for UITableView Hey Kids, today I want to tell you about library I wrote. It works like a tiny little database (without search), it is reusable within the application as all entries are stored in their own file. The entire library is based on NSArray (table) and NSDictionary (row). This library should cover all the main functions everyone needs when working with tables, including easy reordering system. This library is not designed for large amount of data, but up to 100 items should be fine. Sorry for calling this thing a db but Ive had no better idea ... Ok, lets start with the functions that are available in the library. Two files that are included in the header file are included in the archive, which can be downloaded in the top right corner of this tutorial. I will describe what each function is doing later. // // wgFileDb.h // iBBClone // // Created by Ondrej Rafaj on 21.2.10. // Copyright 2010 Home. All rights reserved. // #import #import "wgIo.h" #import "wgPaths.h" #define kFileDbFolder @"wgFileDb" #define kFileDbAIKey @"autoincrement" #define kFileDbDataKey @"data" #define kFileDbIndexKey @"index" #define kMainIdKey @"wgFileDbSecretId" @interface wgFileDb : NSObject { } + (int)getId:(NSDictionary *)item; + (NSString *)getPathToDb:(NSString *)dbName; + (BOOL)isDatabase:(NSString *)dbName; + (BOOL)deleteDb:(NSString *)dbName; + (BOOL)truncateDb:(NSString *)dbName; + (int)getAutoincrementNumberForDb:(NSString *)dbName; + (NSArray *)getItemsFromDb:(NSString *)dbName; + (void)saveFullData:(NSArray *)arr toDb:(NSString *)dbName; + (int)getNumberOfItemsInDb:(NSString *)dbName; + (NSDictionary *)getItem:(int)idItem inDb:(NSString *)dbName; + (int)addItemToBottom:(NSDictionary *)item intoDb:(NSString *)dbName; + (int)addItemToTop:(NSDictionary *)item intoDb:(NSString *)dbName; + (void)deleteItem:(int)idItem fromDb:(NSString *)dbName; + (void)updateItem:(int)idItem withData:(NSDictionary *)item inDb:(NSString *)dbName; + (void)moveTableItem:(NSIndexPath *)fromIndexPath to:(NSIndexPath *)toIndexPath inDb:(NSString *)dbName; + (int)duplicateItemToBottom:(int)idItem inDb:(NSString *)dbName; + (int)duplicateItemToTop:(int)idItem inDb:(NSString *)dbName; @end As you can see from the list, there is quite few methods that are giving you an opportunity to manage almost any dataset. The only thing I want to add is sorting. Here you can see the .m file with all the comments that are relevant to each function. // // wgFileDb.m // iBBClone // // Created by Ondrej Rafaj on 21.2.10. // Copyright 2010 Home. All rights reserved. // #import "wgFileDb.h" @implementation wgFileDb // public; returns id from given object + (int)getId:(NSDictionary *)item { return [[item objectForKey:kMainIdKey] intValue]; } // private; converts number to number in the string + (NSString *)_encodeNumber:(int)num { return [NSString stringWithFormat:@"%d", num]; } // private; returns path to the documents/db folder + (NSString *)_getPathToDb:(NSString *)dbName { NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths databaseDirectory], kFileDbFolder]; [wgIo createFolderIfNotExists:p]; return [NSString stringWithFormat:@"%@%@", p, dbName]; } // public; returns path to the documents/db folder, creates the path if this one doesn''t exist and creates empty db file + (NSString *)getPathToDb:(NSString *)dbName { NSString *p = [self _getPathToDb:dbName]; if (![wgIo isFile:p]) { NSMutableDictionary *d = [[[NSMutableDictionary alloc] init] autorelease]; [d setObject:[self _encodeNumber:0] forKey:kFileDbAIKey]; [d setObject:[[[NSMutableArray alloc] init] autorelease] forKey:kFileDbDataKey]; [d setObject:[[[NSMutableDictionary alloc] init] autorelease] forKey:kFileDbIndexKey]; [d writeToFile:p atomically:YES]; if (![wgIo isFile:p]) NSAssert(@"Unable to create database ''%@'' in wgFileDb.", dbName); } return p; } // public; checks if database file exists + (BOOL)isDatabase:(NSString *)dbName { NSString *p = [self _getPathToDb:dbName]; return [wgIo isFile:p]; } // private; returns full database file + (NSDictionary *)_getFullDataForDb:(NSString *)dbName { NSString *p = [self getPathToDb:dbName]; return [[[NSDictionary alloc] initWithContentsOfFile:p] autorelease]; } // public; deletes the database file + (BOOL)deleteDb:(NSString *)dbName { NSString *p = [self getPathToDb:dbName]; if ([wgIo isFile:p]) return [wgIo deleteFile:p]; else return YES; } // public; truncates the databse fil and sets autoincrement value to zero + (BOOL)truncateDb:(NSString *)dbName { [self deleteDb:dbName]; NSString *p = [self getPathToDb:dbName]; return [wgIo isFile:p]; } // public; returns value of actual autoincrement number + (int)getAutoincrementNumberForDb:(NSString *)dbName { NSDictionary *d = [self _getFullDataForDb:dbName]; return (int) [[d objectForKey:kFileDbAIKey] intValue]; } // public; returns all items from the db as NSArray + (NSArray *)getItemsFromDb:(NSString *)dbName { NSDictionary *d = [self _getFullDataForDb:dbName]; return (NSArray *) [d objectForKey:kFileDbDataKey]; } // private; converts id of the item to index position in the array + (int)_getIndexForId:(int)idItem inDb:(NSString *)dbName { NSDictionary *d = [self _getFullDataForDb:dbName]; return (int) [[[d objectForKey:kFileDbIndexKey] objectForKey:[self _encodeNumber:idItem]] intValue]; } // private; recreates index for all the id''s and items in the db + (void)_reloadIndexTableInDb:(NSString *)dbName { NSMutableDictionary *f = (NSMutableDictionary *)[self _getFullDataForDb:dbName]; NSArray *arr = (NSArray *)[f objectForKey:kFileDbDataKey]; NSMutableDictionary *n = [[[NSMutableDictionary alloc] init] autorelease]; int index = 0; for (NSDictionary *d in arr) { [n setObject:[self _encodeNumber:index] forKey:[d objectForKey:kMainIdKey]]; index++; } [f setObject:n forKey:kFileDbIndexKey]; NSString *p = [self getPathToDb:dbName]; [f writeToFile:p atomically:YES]; } // private; increases autoincrement value for the databse + (int)_increaseAIinDb:(NSString *)dbName { NSMutableDictionary *f = (NSMutableDictionary *)[self _getFullDataForDb:dbName]; int ai = ([self getAutoincrementNumberForDb:dbName] + 1); [f setObject:[self _encodeNumber:ai] forKey:kFileDbAIKey]; NSString *p = [self getPathToDb:dbName]; [f writeToFile:p atomically:YES]; return ai; } // public, saves the full given array back to he database + (void)saveFullData:(NSArray *)arr toDb:(NSString *)dbName { NSMutableDictionary *f = (NSMutableDictionary *)[self _getFullDataForDb:dbName]; [f setObject:arr forKey:kFileDbDataKey]; NSString *p = [self getPathToDb:dbName]; [f writeToFile:p atomically:YES]; [self _reloadIndexTableInDb:dbName]; } // public; returns number of items in the selected db + (int)getNumberOfItemsInDb:(NSString *)dbName { NSArray *arr = [self getItemsFromDb:dbName]; return [arr count]; } // public; returns one item with specific id + (NSDictionary *)getItem:(int)idItem inDb:(NSString *)dbName { NSArray *arr = [self getItemsFromDb:dbName]; return (NSDictionary *)[arr objectAtIndex:[self _getIndexForId:idItem inDb:dbName]]; } // public; add given item to the end of the database + (int)addItemToBottom:(NSDictionary *)item intoDb:(NSString *)dbName { NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName]; int i = [self _increaseAIinDb:dbName]; NSMutableDictionary *nd = (NSMutableDictionary *)item; [nd setObject:[self _encodeNumber:i] forKey:kMainIdKey]; [arr addObject:nd]; [self saveFullData:arr toDb:dbName]; return i; } // public; add given item to the beginning of the database + (int)addItemToTop:(NSDictionary *)item intoDb:(NSString *)dbName { NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName]; NSMutableArray *narr = [[[NSMutableArray alloc] init] autorelease]; int i = [self _increaseAIinDb:dbName]; NSMutableDictionary *nd = (NSMutableDictionary *)item; [nd setObject:[self _encodeNumber:i] forKey:kMainIdKey]; [narr addObject:nd]; for (NSDictionary *d in arr) [narr addObject:d]; [self saveFullData:narr toDb:dbName]; return i; } // public; deletes id specific item from the database + (void)deleteItem:(int)idItem fromDb:(NSString *)dbName { NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName]; [arr removeObjectAtIndex:[self _getIndexForId:idItem inDb:dbName]]; [self saveFullData:arr toDb:dbName]; } // public; updates id specific item in the database + (void)updateItem:(int)idItem withData:(NSDictionary *)item inDb:(NSString *)dbName { NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName]; NSMutableArray *narr = [[[NSMutableArray alloc] init] autorelease]; int ai; for (NSDictionary *d in arr) { ai = [[d objectForKey:kMainIdKey] intValue]; if (ai == idItem) [narr addObject:item]; else [narr addObject:d]; } [self saveFullData:narr toDb:dbName]; } // public; moves item in the db, the input values are the same that are coming from: // - (void)tableView:(UITableView *)tableView moveRowAtIndexPath:(NSIndexPath *)fromIndexPath toIndexPath:(NSIndexPath *)toIndexPath + (void)moveTableItem:(NSIndexPath *)fromIndexPath to:(NSIndexPath *)toIndexPath inDb:(NSString *)dbName { NSMutableArray *arr = (NSMutableArray *) [self getItemsFromDb:dbName]; NSDictionary *i = [[arr objectAtIndex:fromIndexPath.row] retain]; [arr removeObjectAtIndex:fromIndexPath.row]; [arr insertObject:i atIndex:toIndexPath.row]; [self saveFullData:arr toDb:dbName]; } // public; duplicates id specific item in the database and adds this one to the end of db + (int)duplicateItemToBottom:(int)idItem inDb:(NSString *)dbName { NSMutableDictionary *d = (NSMutableDictionary *)[self getItem:idItem inDb:dbName]; [d removeObjectForKey:kMainIdKey]; return [self addItemToBottom:d intoDb:dbName]; } // public; duplicates id specific item in the database and adds this one to the beginning of db + (int)duplicateItemToTop:(int)idItem inDb:(NSString *)dbName { NSMutableDictionary *d = (NSMutableDictionary *)[self getItem:idItem inDb:dbName]; [d removeObjectForKey:kMainIdKey]; return [self addItemToTop:d intoDb:dbName]; } // method in development, comments welcome + (NSArray *)sortAscendingByKey:(NSString *)key inDb:(NSString *)dbName { return [self getItemsFromDb:dbName]; } // method in development, comments welcome + (NSArray *)sortDescendingByKey:(NSString *)key inDb:(NSString *)dbName { return [self getItemsFromDb:dbName]; } @end In the end, there are the file handling libraries Im using to unify paths and check for existing files. File handling library: // // wgIo.h // iDeviant // // Created by Ondrej Rafaj on 26.11.09. // Copyright 2009 Home. All rights reserved. // #import #import "wgPaths.h" @interface wgIo : NSObject { } + (NSString *)readFile:(NSString *)filePath; + (NSDictionary *)getFileAttributes:(NSString *)path; + (NSMutableArray *)getFilesArray:(NSString *)path; + (int)getFileSize:(NSString *)path; + (NSString *)getFileExtension:(NSString *)path; + (NSString *)getFormatedFileSize:(NSString *)path; + (NSString *)getFileCreated:(NSString *)path; + (BOOL)createFolderIfNotExists:(NSString *)path; + (BOOL)isDirectory:(NSString *)path; + (BOOL)isFile:(NSString *)path; + (BOOL)deleteFile:(NSString *)path; @end Main library file: // // wgIo.m // iDeviant // // Created by Ondrej Rafaj on 26.11.09. // Copyright 2009 Home. All rights reserved. // #import "wgIo.h" @implementation wgIo + (NSString *)readFile:(NSString *)filePath { NSError *error; return [NSString stringWithContentsOfFile:filePath encoding:NSUTF8StringEncoding error:&error]; } + (BOOL)isDirectory:(NSString *)path { BOOL isDir; NSFileManager *fileManager = [NSFileManager defaultManager]; if ([fileManager fileExistsAtPath:path isDirectory:&isDir] && isDir) return YES; else return NO; } + (BOOL)isFile:(NSString *)path { BOOL isDir; NSFileManager *fileManager = [NSFileManager defaultManager]; if ([fileManager fileExistsAtPath:path isDirectory:&isDir]) { if (isDir) return NO; else return YES; } else return NO; } + (BOOL)deleteFile:(NSString *)path { NSFileManager *fileManager = [NSFileManager defaultManager]; return [fileManager removeItemAtPath:path error:nil]; } + (NSDictionary *)getFileAttributes:(NSString *)path { NSFileManager *manager = [NSFileManager defaultManager]; NSDictionary *fileAttributes = [manager fileAttributesAtPath:path traverseLink:false]; return fileAttributes; } + (int)getFileSize:(NSString *)path { NSDictionary *fileAttributes = [self getFileAttributes:path]; int ret = [[fileAttributes objectForKey:NSFileSize] intValue]; return ret; } + (NSString *)getFileExtension:(NSString *)path { NSString *ret = [path pathExtension]; return ret; } + (NSString *)getFileCreated:(NSString *)path { NSDictionary *fileAttributes = [self getFileAttributes:path]; NSDate *fileCreationDate = [fileAttributes objectForKey:NSFileCreationDate]; NSString *fileCreationDateString = [fileCreationDate description]; return fileCreationDateString; } + (NSString *)getFormatedFileSize:(NSString *)path { int fileSize = [self getFileSize:path]; NSString *extension; NSString *formatedFileSize; float ff; if (fileSize < 1024) { formatedFileSize = [NSString stringWithFormat:@"%d", fileSize]; extension = @"bytes"; } else if (fileSize < 1048576) { ff = fileSize / 1024; formatedFileSize = [NSString stringWithFormat:@"%.2f", ff]; extension = @"Kb"; } else { ff = fileSize / 1048576; formatedFileSize = [NSString stringWithFormat:@"%.2f", ff]; extension = @"Mb"; } return [NSString stringWithFormat:@"%@ %@", formatedFileSize, extension]; } + (NSMutableArray *)getFilesArray:(NSString *)path { NSMutableArray *fileArray = [[[NSMutableArray alloc] init] autorelease]; NSFileManager *manager = [NSFileManager defaultManager]; NSArray *fileList = [manager directoryContentsAtPath:[wgPaths filesDirectory]]; for (NSString *file in fileList){ if (![file isEqualToString:@"~"]) [fileArray addObject:file]; } return fileArray; } + (BOOL)createFolderIfNotExists:(NSString *)path { if (![self isDirectory:path]) { NSFileManager *fileManager = [NSFileManager defaultManager]; //NSDictionary *dic = [NSDictionary dictionaryWithObjectsAndKeys: [NSDate date], NSFileModificationDate, @"owner", @"NSFileOwnerAccountName", @"group", @"NSFileGroupOwnerAccountName", nil, @"NSFilePosixPermissions", [NSNumber numberWithBool:YES], @"NSFileExtensionHidden", nil]; return [fileManager createDirectoryAtPath:path attributes:nil]; } else return YES; } @end And heres the library that handles paths. Header file: // // wgPaths.h // iDeviant // // Created by Ondrej Rafaj on 26.11.09. // Copyright 2009 Home. All rights reserved. // #import #import "wgIo.h" @interface wgPaths : NSObject { } + (NSString *)tempDirectory; + (NSString *)confDirectory; + (NSString *)documentsDirectory; + (NSString *)filesDirectory; + (NSString *)webDirectory; + (NSString *)databaseDirectory; + (NSString *)systemDirectory; + (NSString *)cacheDirectory; + (NSString *)imageCacheDirectory; + (NSString *)imageCacheDirectory:(NSString *)subFolder; + (NSString *)sqlFile:(NSString *)databaseName; @end And at last the main lib file: // // wgPaths.m // iDeviant // // Created by Ondrej Rafaj on 26.11.09. // Copyright 2009 Home. All rights reserved. // #import "wgPaths.h" #define kSysFolderName @"~" @implementation wgPaths + (NSString *)documentsDirectory { NSArray *paths = NSSearchPathForDirectoriesInDomains(NSDocumentDirectory, NSUserDomainMask, YES); [wgIo createFolderIfNotExists:[NSString stringWithFormat:@"%@/%@/", [paths objectAtIndex:0], kSysFolderName]]; return [paths objectAtIndex:0]; } + (NSString *)confDirectory { NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"config"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)filesDirectory { NSString *p = [NSString stringWithFormat:@"%@/%@/", [self documentsDirectory], @"files"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)tempDirectory { NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"temp"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)systemDirectory { NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"system"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)cacheDirectory { NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths systemDirectory], @"cache"]; if (![wgIo createFolderIfNotExists:p]) NSLog(@"Error creating directory: %@", p); return p; } + (NSString *)imageCacheDirectory { NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths cacheDirectory], @"imagecache"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)imageCacheDirectory:(NSString *)subFolder { NSString *p = [NSString stringWithFormat:@"%@%@/", [wgPaths imageCacheDirectory], subFolder]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)databaseDirectory { NSString *p = [NSString stringWithFormat:@"%@/%@/%@/", [self documentsDirectory], kSysFolderName, @"db"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)webDirectory { NSString *p = [NSString stringWithFormat:@"%@/~/%@/", [self documentsDirectory], @"htdocs"]; [wgIo createFolderIfNotExists:p]; return p; } + (NSString *)sqlFile:(NSString *)databaseName { NSString *p = [NSString stringWithFormat:@"%@%@.sqlite3", [self databaseDirectory], databaseName]; [wgIo createFolderIfNotExists:p]; return p; } @end Thanks for reading, some tutorial about how to use those libraries with full functionality of UITableView (add, delete, reorder and edit) will follow shortly than stay tuned. :) Have nice day, Ondrej', 0, 0, 0, 0, 0, 0),
(52, 1, 0, 1, 8, 0, '2010-03-21 03:24:40', '2010-04-13 01:28:02', '<a href="http://www.ibbclone.com/" class="exitlink">iBBClone home page - http://www.ibbclone.com/</a>\r\n<p>For each visitor, BBClone can display</p>\r\n<p>&nbsp;</p>\r\n<ul>\r\n    <li>* IP address,</li>\r\n    <li>* hostname,</li>\r\n    <li>* operating system,</li>\r\n    <li>* robots,</li>\r\n    <li>* browser,</li>\r\n    <li>* referring URL (where do they come from),</li>\r\n    <li>* visit date,</li>\r\n    <li>* number of time the visitor has loaded the page,</li>\r\n    <li>* number of visitor</li>\r\n    <li>* the visited pages in the order someone viewed them</li>\r\n    <li>* the last visited page</li>\r\n    <li>* the search engine query that lead to your site (if applicable)</li>\r\n    <li>* ranking of the most frequent countries, referrers, OS, browsers, robots, page views and hostnames</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n<p>Other features include:</p>\r\n<ul>\r\n\r\n    <li>* Easy customisation of font sizes, number of columns and their order, the number of icons to be displayed, duration of a unique visit.</li>\r\n    <li>* 32 translations: Arabic, Bosnian, Bulgarian, Catalan, Czech, Chinese Simplified, Chinese Traditional, Danish, Dutch, English, Finnish, French, German, Greek, Hungarian, Italian, Japanese, Korean, Lithuanian, Macedonian, Norwegian (Bokml), Polish, Portuguese, Romanian, Russian, Slovak, Slovenian, Spanish, Swedish, Thai, Turkish and Ukrainian</li>\r\n    <li>* Easy integration into MovableType, Pivot, Drupal, Textpattern, Wordpress, e107, CMSimple, CMComplex and possibly some more applications</li>\r\n    <li>* Standard Compliance: BBClone conforms to XHTML 1.0 and CSS recommendations and doesn''t lock out particular browsers or operating systems</li>\r\n    <li>* no database required</li>\r\n    <li>* reload resistance</li>\r\n    <li>* hostname resolution (can be turned off)</li>\r\n    <li>* time statistics (either by hits or unique visits)</li>\r\n    <li>* proxy workaround and display</li>\r\n    <li>* Whois lookup of ip addresses</li>\r\n    <li>* exclusion of referrers coming from particular sites</li>\r\n    <li>* support for rel=nofollow flag in referrer links against referrer spam</li>\r\n    <li>* referrer filtering of the ones from the server itself</li>\r\n    <li>* removal of query strings to get more useful summarized rankings</li>\r\n    <li>* number of visits by a visitor</li>\r\n    <li>* logging routine optimized to run fast on most websites</li>\r\n    <li>* Exclusion of IPs or IP ranges from being counted</li>\r\n    <li>* Ranking of the most frequent keywords from search engines</li>\r\n    <li>* Accurate country allocation of visitors by using its own lookup database</li>\r\n    <li>* Visualisation of visitors by using icons</li>\r\n</ul>\r\n\r\n', 'iBBClone - popular tracking tool in the palm of your hand', 'ibbclone-popular-tracking-tool-in-the-palm-of-your-hand', 0, '<p>BBclone is a web counter written in PHP and gives a detailed view of the visitors of your web site by displaying the nth last users (and the data they provided, like their IP, browser and so on) that visited the web site, not just showing a number of visitors. This is very handy for webmasters that want to see who is visiting their sites, what browser people use, where they came from etc.</p><p></p>', 0, '', '', '2010-04-13 01:28:02', '2010-04-13 01:28:02', 'iBBClone - popular tracking tool in the palm of your hand BBclone is a web counter written in PHP and gives a detailed view of the visitors of your web site by displaying the nth last users (and the data they provided, like their IP, browser and so on) that visited the web site, not just showing a number of visitors. This is very handy for webmasters that want to see who is visiting their sites, what browser people use, where they came from etc. iBBClone home page - http://www.ibbclone.com/ For each visitor, BBClone can display &nbsp; * IP address, * hostname, * operating system, * robots, * browser, * referring URL (where do they come from), * visit date, * number of time the visitor has loaded the page, * number of visitor * the visited pages in the order someone viewed them * the last visited page * the search engine query that lead to your site (if applicable) * ranking of the most frequent countries, referrers, OS, browsers, robots, page views and hostnames &nbsp; Other features include: * Easy customisation of font sizes, number of columns and their order, the number of icons to be displayed, duration of a unique visit. * 32 translations: Arabic, Bosnian, Bulgarian, Catalan, Czech, Chinese Simplified, Chinese Traditional, Danish, Dutch, English, Finnish, French, German, Greek, Hungarian, Italian, Japanese, Korean, Lithuanian, Macedonian, Norwegian (Bokml), Polish, Portuguese, Romanian, Russian, Slovak, Slovenian, Spanish, Swedish, Thai, Turkish and Ukrainian * Easy integration into MovableType, Pivot, Drupal, Textpattern, Wordpress, e107, CMSimple, CMComplex and possibly some more applications * Standard Compliance: BBClone conforms to XHTML 1.0 and CSS recommendations and doesn''t lock out particular browsers or operating systems * no database required * reload resistance * hostname resolution (can be turned off) * time statistics (either by hits or unique visits) * proxy workaround and display * Whois lookup of ip addresses * exclusion of referrers coming from particular sites * support for rel=nofollow flag in referrer links against referrer spam * referrer filtering of the ones from the server itself * removal of query strings to get more useful summarized rankings * number of visits by a visitor * logging routine optimized to run fast on most websites * Exclusion of IPs or IP ranges from being counted * Ranking of the most frequent keywords from search engines * Accurate country allocation of visitors by using its own lookup database * Visualisation of visitors by using icons', 0, 0, 0, 0, 0, 0);
