(53, 1, 0, 1, 1, 0, '2010-04-12 18:07:58', '2010-04-13 01:27:43', '<p>Today, I''ll show you one of the classes I''ve built for this project, this library will help you to build and display horizontal UIScrollView with unlimited images that you can supply from an array that contains your UIImages.</p>\r\n\r\n<p>First of all these are the files you have to include into your project if you are not using the entire static library that can be found here:</p>\r\n\r\n<p><a href="http://code.google.com/p/iphone-guru-uilibrary/" class="exitlink">http://code.google.com/p/iphone-guru-uilibrary/</a></p>\r\n\r\n<p><strong>Header file:</strong></p>\r\n\r\n<pre>\r\n///\r\n///  IGUIScrollViewImage.h\r\n///\r\n///  IGUILibrary\r\n///\r\n///  Created by Ondrej Rafaj on 7.4.10.\r\n///\r\n///  Copyright 2010 Home. All rights reserved.\r\n///\r\n/// \r\n\r\n\r\n#import &lt;Foundation/Foundation.h>\r\n#import &lt;UIKit/UIKit.h>\r\n\r\n\r\n@interface IGUIScrollViewImage : NSObject &lt;UIScrollViewDelegate> {\r\n	\r\n	UIScrollView *scrollView;\r\n	UIPageControl *pageControl;\r\n	\r\n	CGRect rectScrollView;\r\n	CGRect rectPageControl;\r\n	\r\n	int scrollWidth;\r\n	int scrollHeight;\r\n	\r\n	NSArray *contentArray;\r\n	\r\n	UIColor *bcgColor;\r\n	\r\n	BOOL pageControlEnabledTop;\r\n	BOOL pageControlEnabledBottom;\r\n	\r\n	BOOL rememberPosition;\r\n	NSString *positionIdentifier;\r\n	\r\n}\r\n\r\n@property (nonatomic, retain) UIScrollView *scrollView;\r\n\r\n/// returns width of the scollview\r\n- (int)getScrollViewWidth;\r\n\r\n/// set width and height for your final UIScrollView\r\n- (void)setWidth:(int)width andHeight:(int)height;\r\n\r\n/// set the exactly same size as it is your parent view\r\n- (void)setSizeFromParentView:(UIScrollView *)scView;\r\n\r\n/// set background color for your UIScrollView\r\n- (void)setBackGroudColor:(UIColor *)color;\r\n\r\n/// set an array with images you want to display in your new scroll view\r\n- (void)setContentArray:(NSArray *)images;\r\n\r\n/// display page control for the scroll view on the top of the view (inset)\r\n- (void)enablePageControlOnTop;\r\n\r\n/// display page control for the scroll view on the bottom of the view (inset)\r\n- (void)enablePageControlOnBottom;\r\n\r\n/// enable position history\r\n- (void)enablePositionMemory;\r\n\r\n/// enable position history with custom memory identifier\r\n- (void)enablePositionMemoryWithIdentifier:(NSString *)identifier;\r\n\r\n/// returns your UIScrollView with predefined page\r\n- (UIScrollView *)getWithPosition:(int)page;\r\n\r\n/// returns your UIScrollView with enabled position history\r\n- (UIScrollView *)getWithPositionMemory;\r\n\r\n/// returns your UIScrollView with enabled position history with custom memory identifier\r\n- (UIScrollView *)getWithPositionMemoryIdentifier:(NSString *)identifier;\r\n\r\n/// returns your UIScrollView\r\n- (UIScrollView *)get;\r\n\r\n@end\r\n\r\n</pre>\r\n\r\n<p><strong>Implementation file:</strong></p>\r\n\r\n<pre>\r\n//\r\n//  IGUIScrollViewImage.m\r\n//  IGUILibrary\r\n//\r\n//  Created by Ondrej Rafaj on 7.4.10.\r\n//  Copyright 2010 Home. All rights reserved.\r\n//\r\n\r\n#import "IGUIScrollViewImage.h"\r\n\r\n#define kIGUIScrollViewImagePageIdentifier						@"kIGUIScrollViewImagePageIdentifier"\r\n#define kIGUIScrollViewImageDefaultPageIdentifier				@"Default"\r\n\r\n\r\n@implementation IGUIScrollViewImage\r\n\r\n@synthesize scrollView;\r\n\r\n- (int)getScrollViewWidth {\r\n	return ([contentArray count] * scrollWidth);\r\n}\r\n\r\n- (void)setWidth:(int)width andHeight:(int)height {\r\n	scrollWidth = width;\r\n	scrollHeight = height;\r\n	if (!width || !height) rectScrollView = [[UIScreen mainScreen] applicationFrame];\r\n	else rectScrollView = CGRectMake(0, 0, width, height);\r\n}\r\n\r\n- (void)setSizeFromParentView:(UIScrollView *)scView {\r\n	scrollWidth = scView.frame.size.width;\r\n	scrollHeight = scView.frame.size.height;\r\n	rectScrollView = CGRectMake(0, 0, scrollWidth, scrollHeight);\r\n}\r\n\r\n- (void)setContentArray:(NSArray *)images {\r\n	contentArray = images;\r\n}\r\n\r\n- (void)setBackGroudColor:(UIColor *)color {\r\n	bcgColor = color;\r\n}\r\n\r\n- (void)enablePageControlOnTop {\r\n	pageControlEnabledTop = YES;\r\n}\r\n\r\n- (void)enablePageControlOnBottom {\r\n	pageControlEnabledBottom = YES;\r\n}\r\n\r\n- (void)enablePositionMemoryWithIdentifier:(NSString *)identifier {\r\n	rememberPosition = YES;\r\n	if (!identifier) identifier = kIGUIScrollViewImageDefaultPageIdentifier;\r\n	positionIdentifier = identifier;\r\n}\r\n\r\n- (void)enablePositionMemory {\r\n	[self enablePositionMemoryWithIdentifier:nil];\r\n}\r\n\r\n- (UIScrollView *)getWithPosition:(int)page {\r\n	if (!contentArray) {\r\n		contentArray = [[[NSArray alloc] init] autorelease];\r\n	}\r\n	if (page > [contentArray count]) page = 0;\r\n	\r\n	if (!scrollWidth || !scrollHeight) {\r\n		rectScrollView = [[UIScreen mainScreen] applicationFrame];\r\n		scrollWidth = rectScrollView.size.width;\r\n		scrollHeight = rectScrollView.size.height;\r\n	}\r\n	rectScrollView = CGRectMake(0, 0, scrollWidth, scrollHeight);\r\n	 \r\n	self.scrollView = [[UIScrollView alloc] initWithFrame:rectScrollView];\r\n    self.scrollView.contentSize = CGSizeMake([self getScrollViewWidth], scrollHeight);\r\n	if (!bcgColor) bcgColor = [UIColor blackColor];\r\n	self.scrollView.backgroundColor = bcgColor;\r\n	self.scrollView.alwaysBounceHorizontal = YES;\r\n	self.scrollView.contentOffset = CGPointMake(page * scrollWidth, 0);\r\n	self.scrollView.pagingEnabled = YES;\r\n	\r\n	UIView *main = [[[UIView alloc] initWithFrame:rectScrollView] autorelease];\r\n	int i = 0;\r\n	for (UIImage *img in contentArray) {\r\n		UIImageView *imageView = [[UIImageView alloc] init];\r\n		imageView.image = img;\r\n		imageView.contentMode = UIViewContentModeScaleAspectFit;\r\n		imageView.autoresizingMask = (UIViewAutoresizingFlexibleWidth | UIViewAutoresizingFlexibleHeight);\r\n		imageView.backgroundColor = [UIColor blackColor];\r\n		float ratio = img.size.width/rectScrollView.size.width;\r\n		CGRect imageFrame = CGRectMake(i, 0, rectScrollView.size.width, (img.size.height / ratio));\r\n		imageView.frame = imageFrame;\r\n		[self.scrollView addSubview:(UIView *)imageView];\r\n		i += scrollWidth;\r\n		[imageView release];\r\n	}\r\n	[main addSubview:scrollView];\r\n	if (pageControlEnabledTop) {\r\n		rectPageControl = CGRectMake(0, 5, scrollWidth, 15);\r\n	}\r\n	else if (pageControlEnabledBottom) {\r\n		rectPageControl = CGRectMake(0, (scrollHeight - 25), scrollWidth, 15);\r\n	}\r\n	if (pageControlEnabledTop || pageControlEnabledBottom) {\r\n		pageControl = [[[UIPageControl alloc] initWithFrame:rectPageControl] autorelease];\r\n		pageControl.numberOfPages = [contentArray count];\r\n		pageControl.currentPage = page;\r\n		[main addSubview:pageControl];\r\n	}\r\n	if (pageControlEnabledTop || pageControlEnabledBottom || rememberPosition) self.scrollView.delegate = self;\r\n	//if (margin) [margin release];\r\n	return (UIScrollView *)main;\r\n}\r\n\r\n- (UIScrollView *)get {\r\n	return [self getWithPosition:0];\r\n}\r\n\r\n- (UIScrollView *)getWithPositionMemory {\r\n	[self enablePositionMemory];\r\n	return [self getWithPosition:[[[NSUserDefaults standardUserDefaults] objectForKey:[NSString stringWithFormat:@"%@%@", kIGUIScrollViewImagePageIdentifier, kIGUIScrollViewImageDefaultPageIdentifier]] intValue]];\r\n}\r\n\r\n- (UIScrollView *)getWithPositionMemoryIdentifier:(NSString *)identifier {\r\n	[self enablePositionMemoryWithIdentifier:identifier];\r\n	return [self getWithPosition:[[[NSUserDefaults standardUserDefaults] objectForKey:[NSString stringWithFormat:@"%@%@", kIGUIScrollViewImagePageIdentifier, positionIdentifier]] intValue]];\r\n}\r\n\r\n- (void)scrollViewDidEndDecelerating:(UIScrollView *)sv {\r\n	int page = sv.contentOffset.x / sv.frame.size.width;\r\n	pageControl.currentPage = page;\r\n	if (rememberPosition) {\r\n		[[NSUserDefaults standardUserDefaults] setObject:[NSString stringWithFormat:@"%d", page] forKey:[NSString stringWithFormat:@"%@%@", kIGUIScrollViewImagePageIdentifier, positionIdentifier]];\r\n		[[NSUserDefaults standardUserDefaults] synchronize];\r\n	}\r\n}\r\n\r\n- (void)dealloc {\r\n	[scrollView release];\r\n	[super dealloc];\r\n}\r\n\r\n\r\n@end\r\n\r\n</pre>\r\n\r\n<p>And now just a short example of how to use this class (of course you have to have all those images listed below in your application bundle):</p>\r\n\r\n<pre>\r\n- (NSArray *)getImages {\r\n        NSMutableArray *arr = [[[NSMutableArray alloc] init] autorelease];\r\n        [arr addObject:[UIImage imageNamed:@"1.jpg"]];\r\n        [arr addObject:[UIImage imageNamed:@"2.jpg"]];\r\n        [arr addObject:[UIImage imageNamed:@"3.jpg"]];\r\n        [arr addObject:[UIImage imageNamed:@"4.jpg"]];\r\n        [arr addObject:[UIImage imageNamed:@"5.jpg"]];\r\n        [arr addObject:[UIImage imageNamed:@"6.jpg"]];\r\n        [arr addObject:[UIImage imageNamed:@"7.jpg"]];\r\n        return (NSArray *)arr;\r\n}\r\n\r\n- (void)viewDidLoad {\r\n        IGUIScrollViewImage *svimage = [[IGUIScrollViewImage alloc] init];\r\n        [svimage setContentArray:[self getImages]];\r\n        [svimage setSizeFromParentView:self.myUIView];\r\n        [svimage enablePageControlOnBottom];\r\n        [self.myUIView addSubview:[svimage getWithPositionMemory]];\r\n}\r\n</pre>\r\n\r\n<p>For more examples please check:</p>\r\n<p><a href="http://code.google.com/p/iphone-guru-uilibrary/wiki/IGUIScrollViewImage" class="exitlink">http://code.google.com/p/iphone-guru-uilibrary/wiki/IGUIScrollViewImage</a></p>\r\n<p>As there might be more updated content ...</p>\r\n\r\n<p>If you have any questions please feel free to contact me anytime trough any form on this website :)</p>\r\n\r\n<p>Cheers,</p>\r\n\r\n<p>Ondrej</p>\r\n', 'How to build UIScrollView with page control', 'how-to-build-uiscrollview-with-page-control', 0, '<p>Few days ago I needed to build horizontal UIScrollView with pagination, then I started thinking about reusing the code later and this is the result. I started Google Code project, it is a static library that is so far handling different types of UIScrollView but we are working on it every day, than stay tuned cause there will be much more to come :)</p>', 1, '', '', '2010-04-13 01:27:43', '2010-04-13 01:27:43', 'How to build UIScrollView with page control Few days ago I needed to build horizontal UIScrollView with pagination, then I started thinking about reusing the code later and this is the result. I started Google Code project, it is a static library that is so far handling different types of UIScrollView but we are working on it every day, than stay tuned cause there will be much more to come :) Today, I''ll show you one of the classes I''ve built for this project, this library will help you to build and display horizontal UIScrollView with unlimited images that you can supply from an array that contains your UIImages. First of all these are the files you have to include into your project if you are not using the entire static library that can be found here: http://code.google.com/p/iphone-guru-uilibrary/ Header file: /// /// IGUIScrollViewImage.h /// /// IGUILibrary /// /// Created by Ondrej Rafaj on 7.4.10. /// /// Copyright 2010 Home. All rights reserved. /// /// #import &lt;Foundation/Foundation.h> #import &lt;UIKit/UIKit.h> @interface IGUIScrollViewImage : NSObject &lt;UIScrollViewDelegate> { UIScrollView *scrollView; UIPageControl *pageControl; CGRect rectScrollView; CGRect rectPageControl; int scrollWidth; int scrollHeight; NSArray *contentArray; UIColor *bcgColor; BOOL pageControlEnabledTop; BOOL pageControlEnabledBottom; BOOL rememberPosition; NSString *positionIdentifier; } @property (nonatomic, retain) UIScrollView *scrollView; /// returns width of the scollview - (int)getScrollViewWidth; /// set width and height for your final UIScrollView - (void)setWidth:(int)width andHeight:(int)height; /// set the exactly same size as it is your parent view - (void)setSizeFromParentView:(UIScrollView *)scView; /// set background color for your UIScrollView - (void)setBackGroudColor:(UIColor *)color; /// set an array with images you want to display in your new scroll view - (void)setContentArray:(NSArray *)images; /// display page control for the scroll view on the top of the view (inset) - (void)enablePageControlOnTop; /// display page control for the scroll view on the bottom of the view (inset) - (void)enablePageControlOnBottom; /// enable position history - (void)enablePositionMemory; /// enable position history with custom memory identifier - (void)enablePositionMemoryWithIdentifier:(NSString *)identifier; /// returns your UIScrollView with predefined page - (UIScrollView *)getWithPosition:(int)page; /// returns your UIScrollView with enabled position history - (UIScrollView *)getWithPositionMemory; /// returns your UIScrollView with enabled position history with custom memory identifier - (UIScrollView *)getWithPositionMemoryIdentifier:(NSString *)identifier; /// returns your UIScrollView - (UIScrollView *)get; @end Implementation file: // // IGUIScrollViewImage.m // IGUILibrary // // Created by Ondrej Rafaj on 7.4.10. // Copyright 2010 Home. All rights reserved. // #import "IGUIScrollViewImage.h" #define kIGUIScrollViewImagePageIdentifier @"kIGUIScrollViewImagePageIdentifier" #define kIGUIScrollViewImageDefaultPageIdentifier @"Default" @implementation IGUIScrollViewImage @synthesize scrollView; - (int)getScrollViewWidth { return ([contentArray count] * scrollWidth); } - (void)setWidth:(int)width andHeight:(int)height { scrollWidth = width; scrollHeight = height; if (!width || !height) rectScrollView = [[UIScreen mainScreen] applicationFrame]; else rectScrollView = CGRectMake(0, 0, width, height); } - (void)setSizeFromParentView:(UIScrollView *)scView { scrollWidth = scView.frame.size.width; scrollHeight = scView.frame.size.height; rectScrollView = CGRectMake(0, 0, scrollWidth, scrollHeight); } - (void)setContentArray:(NSArray *)images { contentArray = images; } - (void)setBackGroudColor:(UIColor *)color { bcgColor = color; } - (void)enablePageControlOnTop { pageControlEnabledTop = YES; } - (void)enablePageControlOnBottom { pageControlEnabledBottom = YES; } - (void)enablePositionMemoryWithIdentifier:(NSString *)identifier { rememberPosition = YES; if (!identifier) identifier = kIGUIScrollViewImageDefaultPageIdentifier; positionIdentifier = identifier; } - (void)enablePositionMemory { [self enablePositionMemoryWithIdentifier:nil]; } - (UIScrollView *)getWithPosition:(int)page { if (!contentArray) { contentArray = [[[NSArray alloc] init] autorelease]; } if (page > [contentArray count]) page = 0; if (!scrollWidth || !scrollHeight) { rectScrollView = [[UIScreen mainScreen] applicationFrame]; scrollWidth = rectScrollView.size.width; scrollHeight = rectScrollView.size.height; } rectScrollView = CGRectMake(0, 0, scrollWidth, scrollHeight); self.scrollView = [[UIScrollView alloc] initWithFrame:rectScrollView]; self.scrollView.contentSize = CGSizeMake([self getScrollViewWidth], scrollHeight); if (!bcgColor) bcgColor = [UIColor blackColor]; self.scrollView.backgroundColor = bcgColor; self.scrollView.alwaysBounceHorizontal = YES; self.scrollView.contentOffset = CGPointMake(page * scrollWidth, 0); self.scrollView.pagingEnabled = YES; UIView *main = [[[UIView alloc] initWithFrame:rectScrollView] autorelease]; int i = 0; for (UIImage *img in contentArray) { UIImageView *imageView = [[UIImageView alloc] init]; imageView.image = img; imageView.contentMode = UIViewContentModeScaleAspectFit; imageView.autoresizingMask = (UIViewAutoresizingFlexibleWidth | UIViewAutoresizingFlexibleHeight); imageView.backgroundColor = [UIColor blackColor]; float ratio = img.size.width/rectScrollView.size.width; CGRect imageFrame = CGRectMake(i, 0, rectScrollView.size.width, (img.size.height / ratio)); imageView.frame = imageFrame; [self.scrollView addSubview:(UIView *)imageView]; i += scrollWidth; [imageView release]; } [main addSubview:scrollView]; if (pageControlEnabledTop) { rectPageControl = CGRectMake(0, 5, scrollWidth, 15); } else if (pageControlEnabledBottom) { rectPageControl = CGRectMake(0, (scrollHeight - 25), scrollWidth, 15); } if (pageControlEnabledTop || pageControlEnabledBottom) { pageControl = [[[UIPageControl alloc] initWithFrame:rectPageControl] autorelease]; pageControl.numberOfPages = [contentArray count]; pageControl.currentPage = page; [main addSubview:pageControl]; } if (pageControlEnabledTop || pageControlEnabledBottom || rememberPosition) self.scrollView.delegate = self; //if (margin) [margin release]; return (UIScrollView *)main; } - (UIScrollView *)get { return [self getWithPosition:0]; } - (UIScrollView *)getWithPositionMemory { [self enablePositionMemory]; return [self getWithPosition:[[[NSUserDefaults standardUserDefaults] objectForKey:[NSString stringWithFormat:@"%@%@", kIGUIScrollViewImagePageIdentifier, kIGUIScrollViewImageDefaultPageIdentifier]] intValue]]; } - (UIScrollView *)getWithPositionMemoryIdentifier:(NSString *)identifier { [self enablePositionMemoryWithIdentifier:identifier]; return [self getWithPosition:[[[NSUserDefaults standardUserDefaults] objectForKey:[NSString stringWithFormat:@"%@%@", kIGUIScrollViewImagePageIdentifier, positionIdentifier]] intValue]]; } - (void)scrollViewDidEndDecelerating:(UIScrollView *)sv { int page = sv.contentOffset.x / sv.frame.size.width; pageControl.currentPage = page; if (rememberPosition) { [[NSUserDefaults standardUserDefaults] setObject:[NSString stringWithFormat:@"%d", page] forKey:[NSString stringWithFormat:@"%@%@", kIGUIScrollViewImagePageIdentifier, positionIdentifier]]; [[NSUserDefaults standardUserDefaults] synchronize]; } } - (void)dealloc { [scrollView release]; [super dealloc]; } @end And now just a short example of how to use this class (of course you have to have all those images listed below in your application bundle): - (NSArray *)getImages { NSMutableArray *arr = [[[NSMutableArray alloc] init] autorelease]; [arr addObject:[UIImage imageNamed:@"1.jpg"]]; [arr addObject:[UIImage imageNamed:@"2.jpg"]]; [arr addObject:[UIImage imageNamed:@"3.jpg"]]; [arr addObject:[UIImage imageNamed:@"4.jpg"]]; [arr addObject:[UIImage imageNamed:@"5.jpg"]]; [arr addObject:[UIImage imageNamed:@"6.jpg"]]; [arr addObject:[UIImage imageNamed:@"7.jpg"]]; return (NSArray *)arr; } - (void)viewDidLoad { IGUIScrollViewImage *svimage = [[IGUIScrollViewImage alloc] init]; [svimage setContentArray:[self getImages]]; [svimage setSizeFromParentView:self.myUIView]; [svimage enablePageControlOnBottom]; [self.myUIView addSubview:[svimage getWithPositionMemory]]; } For more examples please check: http://code.google.com/p/iphone-guru-uilibrary/wiki/IGUIScrollViewImage As there might be more updated content ... If you have any questions please feel free to contact me anytime trough any form on this website :) Cheers, Ondrej', 0, 0, 0, 0, 0, 0),
