(56, 1, 0, 1, 1, 0, '2011-03-18 14:36:47', '2011-03-18 21:26:01', '<pre>\r\n#pragma mark Adding person to the contacts\r\n\r\n- (void)addContact {\r\n    // Creating new entry\r\n    ABAddressBookRef addressBook = ABAddressBookCreate();\r\n    ABRecordRef person = ABPersonCreate();\r\n    \r\n    // Setting basic properties\r\n    ABRecordSetValue(person, kABPersonFirstNameProperty, @"Ondrej" , nil);\r\n    ABRecordSetValue(person, kABPersonLastNameProperty, @"Rafaj", nil);\r\n    ABRecordSetValue(person, kABPersonJobTitleProperty, @"Tech. director", nil);\r\n    ABRecordSetValue(person, kABPersonDepartmentProperty, @"iPhone development department", nil);\r\n    ABRecordSetValue(person, kABPersonOrganizationProperty, @"Fuerte international", nil);\r\n    ABRecordSetValue(person, kABPersonNoteProperty, @"The best iPhone development studio in the UK :)", nil);\r\n    \r\n    // Adding phone numbers\r\n    ABMutableMultiValueRef phoneNumberMultiValue = ABMultiValueCreateMutable(kABMultiStringPropertyType);\r\n    ABMultiValueAddValueAndLabel(phoneNumberMultiValue, @"07972574949", (CFStringRef)@"iPhone", NULL);\r\n    ABMultiValueAddValueAndLabel(phoneNumberMultiValue, @"01234567890", (CFStringRef)@"Work", NULL);\r\n    ABMultiValueAddValueAndLabel(phoneNumberMultiValue, @"08701234567", (CFStringRef)@"0870", NULL);\r\n    ABRecordSetValue(person, kABPersonPhoneProperty, phoneNumberMultiValue, nil);\r\n    CFRelease(phoneNumberMultiValue);\r\n    \r\n    // Adding url\r\n    ABMutableMultiValueRef urlMultiValue = ABMultiValueCreateMutable(kABMultiStringPropertyType);\r\n    ABMultiValueAddValueAndLabel(urlMultiValue, @"http://www.fuerteint.com", kABPersonHomePageLabel, NULL);\r\n    ABRecordSetValue(person, kABPersonURLProperty, urlMultiValue, nil);\r\n    CFRelease(urlMultiValue);\r\n    \r\n    // Adding emails\r\n    ABMutableMultiValueRef emailMultiValue = ABMultiValueCreateMutable(kABMultiStringPropertyType);\r\n    ABMultiValueAddValueAndLabel(emailMultiValue, @"info@fuerteint.com", (CFStringRef)@"Global", NULL);\r\n    ABMultiValueAddValueAndLabel(emailMultiValue, @"ondrej.rafaj@fuerteint.com", (CFStringRef)@"Work", NULL);\r\n    ABRecordSetValue(person, kABPersonURLProperty, emailMultiValue, nil);\r\n    CFRelease(emailMultiValue);\r\n    \r\n    // Adding address\r\n    ABMutableMultiValueRef addressMultipleValue = ABMultiValueCreateMutable(kABMultiDictionaryPropertyType);\r\n    NSMutableDictionary *addressDictionary = [[NSMutableDictionary alloc] init];\r\n    [addressDictionary setObject:@"8-15 Dereham Place" forKey:(NSString *)kABPersonAddressStreetKey];\r\n    [addressDictionary setObject:@"London" forKey:(NSString *)kABPersonAddressCityKey];\r\n    [addressDictionary setObject:@"EC2A 3HJ" forKey:(NSString *)kABPersonAddressZIPKey];\r\n    [addressDictionary setObject:@"United Kingdom" forKey:(NSString *)kABPersonAddressCountryKey];\r\n    [addressDictionary setObject:@"gb" forKey:(NSString *)kABPersonAddressCountryCodeKey];\r\n    ABMultiValueAddValueAndLabel(addressMultipleValue, addressDictionary, kABHomeLabel, NULL);\r\n    [addressDictionary release];\r\n    ABRecordSetValue(person, kABPersonAddressProperty, addressMultipleValue, nil);\r\n    CFRelease(addressMultipleValue);\r\n    \r\n    // Adding person to the address book\r\n    ABAddressBookAddRecord(addressBook, person, nil);\r\n    CFRelease(addressBook);\r\n    \r\n    // Creating view controller for a new contact\r\n    ABNewPersonViewController *c = [[ABNewPersonViewController alloc] init];\r\n    [c setNewPersonViewDelegate:self];\r\n    [c setDisplayedPerson:person];\r\n    CFRelease(person);\r\n    [self.navigationController pushViewController:c animated:YES];\r\n    [c release];\r\n}\r\n\r\n</pre>\r\n<p>If user wants to save the contact the "person" variable is not nil</p>\r\n<pre>\r\n\r\n#pragma mark Adress book delegate methods\r\n\r\n- (void)newPersonViewController:(ABNewPersonViewController *)newPersonView didCompleteWithNewPerson:(ABRecordRef)person {\r\n    if (person) {\r\n        ABAddressBookRef addressBook = ABAddressBookCreate();\r\n        ABAddressBookAddRecord(addressBook, person, nil);\r\n        ABAddressBookSave(addressBook, nil);\r\n        CFRelease(addressBook);\r\n    }\r\n    [self.navigationController popViewControllerAnimated:YES];\r\n}\r\n\r\n</pre>', 'Best example of how to add entry to contacts using AddressBookUI framework on iPhone SDK', 'best-example-of-how-to-add-entry-to-contacts-using-addressbookui-framework-on-iphone-sdk', 0, '', 1, '', '', '2011-03-18 21:26:01', '2011-03-18 21:26:01', 'Best example of how to add entry to contacts using AddressBookUI framework on iPhone SDK #pragma mark Adding person to the contacts - (void)addContact { // Creating new entry ABAddressBookRef addressBook = ABAddressBookCreate(); ABRecordRef person = ABPersonCreate(); // Setting basic properties ABRecordSetValue(person, kABPersonFirstNameProperty, @"Ondrej" , nil); ABRecordSetValue(person, kABPersonLastNameProperty, @"Rafaj", nil); ABRecordSetValue(person, kABPersonJobTitleProperty, @"Tech. director", nil); ABRecordSetValue(person, kABPersonDepartmentProperty, @"iPhone development department", nil); ABRecordSetValue(person, kABPersonOrganizationProperty, @"Fuerte international", nil); ABRecordSetValue(person, kABPersonNoteProperty, @"The best iPhone development studio in the UK :)", nil); // Adding phone numbers ABMutableMultiValueRef phoneNumberMultiValue = ABMultiValueCreateMutable(kABMultiStringPropertyType); ABMultiValueAddValueAndLabel(phoneNumberMultiValue, @"07972574949", (CFStringRef)@"iPhone", NULL); ABMultiValueAddValueAndLabel(phoneNumberMultiValue, @"01234567890", (CFStringRef)@"Work", NULL); ABMultiValueAddValueAndLabel(phoneNumberMultiValue, @"08701234567", (CFStringRef)@"0870", NULL); ABRecordSetValue(person, kABPersonPhoneProperty, phoneNumberMultiValue, nil); CFRelease(phoneNumberMultiValue); // Adding url ABMutableMultiValueRef urlMultiValue = ABMultiValueCreateMutable(kABMultiStringPropertyType); ABMultiValueAddValueAndLabel(urlMultiValue, @"http://www.fuerteint.com", kABPersonHomePageLabel, NULL); ABRecordSetValue(person, kABPersonURLProperty, urlMultiValue, nil); CFRelease(urlMultiValue); // Adding emails ABMutableMultiValueRef emailMultiValue = ABMultiValueCreateMutable(kABMultiStringPropertyType); ABMultiValueAddValueAndLabel(emailMultiValue, @"info@fuerteint.com", (CFStringRef)@"Global", NULL); ABMultiValueAddValueAndLabel(emailMultiValue, @"ondrej.rafaj@fuerteint.com", (CFStringRef)@"Work", NULL); ABRecordSetValue(person, kABPersonURLProperty, emailMultiValue, nil); CFRelease(emailMultiValue); // Adding address ABMutableMultiValueRef addressMultipleValue = ABMultiValueCreateMutable(kABMultiDictionaryPropertyType); NSMutableDictionary *addressDictionary = [[NSMutableDictionary alloc] init]; [addressDictionary setObject:@"8-15 Dereham Place" forKey:(NSString *)kABPersonAddressStreetKey]; [addressDictionary setObject:@"London" forKey:(NSString *)kABPersonAddressCityKey]; [addressDictionary setObject:@"EC2A 3HJ" forKey:(NSString *)kABPersonAddressZIPKey]; [addressDictionary setObject:@"United Kingdom" forKey:(NSString *)kABPersonAddressCountryKey]; [addressDictionary setObject:@"gb" forKey:(NSString *)kABPersonAddressCountryCodeKey]; ABMultiValueAddValueAndLabel(addressMultipleValue, addressDictionary, kABHomeLabel, NULL); [addressDictionary release]; ABRecordSetValue(person, kABPersonAddressProperty, addressMultipleValue, nil); CFRelease(addressMultipleValue); // Adding person to the address book ABAddressBookAddRecord(addressBook, person, nil); CFRelease(addressBook); // Creating view controller for a new contact ABNewPersonViewController *c = [[ABNewPersonViewController alloc] init]; [c setNewPersonViewDelegate:self]; [c setDisplayedPerson:person]; CFRelease(person); [self.navigationController pushViewController:c animated:YES]; [c release]; } If user wants to save the contact the "person" variable is not nil #pragma mark Adress book delegate methods - (void)newPersonViewController:(ABNewPersonViewController *)newPersonView didCompleteWithNewPerson:(ABRecordRef)person { if (person) { ABAddressBookRef addressBook = ABAddressBookCreate(); ABAddressBookAddRecord(addressBook, person, nil); ABAddressBookSave(addressBook, nil); CFRelease(addressBook); } [self.navigationController popViewControllerAnimated:YES]; }', 0, 0, 0, 0, 0, 0),
(57, 1, 0, 1, 1, 1, '2011-06-28 11:45:07', '2011-06-28 11:50:52', 'I''ve been following the official Apple help guide and it works now ... there might be still some issue with intellisense but IT WORKS :)\r\n\r\nJust one little note, when you download the example file, make sure you build the static lib first and than re-link it back to the main app project ... You can''t miss it as it will be in red :)', 'XCode 4 and static libraries', 'xcode-4-and-static-libraries', 0, 'So I finally found a way how to use workspaces and static libraries', 0, '', '', '2011-06-28 11:50:52', '2011-06-28 11:50:52', 'XCode 4 and static libraries So I finally found a way how to use workspaces and static libraries I''ve been following the official Apple help guide and it works now ... there might be still some issue with intellisense but IT WORKS :) Just one little note, when you download the example file, make sure you build the static lib first and than re-link it back to the main app project ... You can''t miss it as it will be in red :)', 0, 0, 0, 0, 0, 0),
(58, 1, 0, 1, 1, 7, '2011-10-19 13:44:14', '2011-10-19 13:52:25', '<h1>Implementation Instructions:</h1>\r\n\r\n<ul>\r\n<li>Import SystemConfiguration, CFNetwork, MobileCoreServices, MEssageUI frameworks to your library </li>\r\n<li>Create an Instance of FTShare on your Application Delegate</li>\r\n<li>Overwrite the method application:openURL:sourceApplication:annotation: on your Application Delegate</li>\r\n<li>Use the Application delegate FTShare instance everywhere in your app by:\r\n\r\n<ul>\r\n<li>setUp the keys for each share components (use config.h)</li>\r\n\r\n<li>Implement the delegates</li>\r\n<li>use shareViaFacebook/Twitter/Mail with the specific FTShareDataObject or</li>\r\n<li>use showActionSheetWithtitle:andOptions: implementing the Facebook/Twitter/MailShareData delegate </li>\r\n</ul>\r\n</li>\r\n<li>on the app-info.plist add the "URL types" (as on picture below). Forget to do so and your app will not respond effectively after the facebook login has completed</li>\r\n</ul><p><img src="https://developers.facebook.com/attachment/ios_config.png" alt="URL types"></p>\r\n\r\n<h1>Third Party references:</h1>\r\n\r\n<ul>\r\n<li>\r\n\r\n<a href="https://github.com/facebook/facebook-ios-sdk" rel="nofollow">facebook-ios-sdk</a> </li>\r\n<li><a href="https://github.com/mattgemmell/MGTwitterEngine" rel="nofollow">MGTwitterEngine</a></li>\r\n<li>Note that SBJson has been modified therefor may or may not work with your version. </li>\r\n</ul>', 'FTShare - an easy way to share using Facebook Twitter and Mails for iOS', 'ftshare-an-easy-way-to-share-using-facebook-twitter-and-mails-for-ios', 0, 'FTshare allows you to set up a share instance on your application delegate and then share using the most common social networks (and more to come). It is as easy as setting up your developer account credential and setting what you want to share.', 1, '', '', '2011-10-19 13:52:25', '2011-10-19 13:52:25', 'FTShare - an easy way to share using Facebook Twitter and Mails for iOS FTshare allows you to set up a share instance on your application delegate and then share using the most common social networks (and more to come). It is as easy as setting up your developer account credential and setting what you want to share. Implementation Instructions: Import SystemConfiguration, CFNetwork, MobileCoreServices, MEssageUI frameworks to your library Create an Instance of FTShare on your Application Delegate Overwrite the method application:openURL:sourceApplication:annotation: on your Application Delegate Use the Application delegate FTShare instance everywhere in your app by: setUp the keys for each share components (use config.h) Implement the delegates use shareViaFacebook/Twitter/Mail with the specific FTShareDataObject or use showActionSheetWithtitle:andOptions: implementing the Facebook/Twitter/MailShareData delegate on the app-info.plist add the "URL types" (as on picture below). Forget to do so and your app will not respond effectively after the facebook login has completed Third Party references: facebook-ios-sdk MGTwitterEngine Note that SBJson has been modified therefor may or may not work with your version.', 0, 0, 0, 0, 0, 0),
