INSERT INTO `csnippets_snippets` (`id`, `name`, `approved`, `snippet`, `excerpt`, `description`, `keywords`, `csnippets_categories_id`, `added`, `changed`, `users_id`, `system_users_id`, `deleted`) VALUES
(1, 'Start link in Safari from your iPhone application', 1, 'NSURL *myUrl = [[NSURL alloc] initWithString: @"http://www.xprogress.com"];\r\n[[UIApplication sharedApplication] openURL:myUrl];', 'Shows how to redirect user from your application to the link in Safari', '', '', 1, '2009-08-06 12:33:44', '2009-08-12 17:35:27', 1, 1, 0),
(2, 'How to load remote file from internet into your Objective-C application', 1, 'NSData *externalData = [NSData dataWithContentsOfURL:[NSURL URLWithString: @"http"//www.xprogress.com/time.php"]]', 'Learn how to load content of the remote file from the internet into NSData variable.', 'In this example we are loading current timestamp on the server', 'loading external data,  ', 1, '2009-08-06 12:58:56', '2009-08-07 18:52:42', 1, 1, 0),
(4, 'Encode URL by adding percent escapes in Objective-C', 1, 'NSString *oldUrl = @"http://www.xprogress.com/post-28-uibutton-tutorial-example-how-to-use-the-button-in-xcode-for-iphones/";\r\nNSString *newUrl = [oldUrl stringByAddingPercentEscapesUsingEncoding:NSUTF8StringEncoding];', 'This snippet will show you how to encode the URL ... that means replacing special characters with their % equivalent.', '<p>The specification for URLs (RFC 1738, Dec. ''94) poses a problem, in that it limits the use of allowed characters in URLs to only a limited subset of the US-ASCII character set:</p>\r\n\r\n<p>"...Only alphanumerics [0-9a-zA-Z], the special characters "$-_.+!*''()," [not including the quotes - ed], and reserved characters used for their reserved purposes may be used unencoded within a URL."</p>\r\n\r\n<p>HTML, on the other hand, allows the entire range of the ISO-8859-1 (ISO-Latin) character set to be used in documents - and HTML4 expands the allowable range to include all of the Unicode character set as well. In the case of non-ISO-8859-1 characters (characters above FF hex/255 decimal in the Unicode set), they just can not be used in URLs, because there is no safe way to specify character set information in the URL content yet [RFC2396.] </p>', 'encode url, escaping string', 1, '2009-08-07 12:11:12', '2009-08-07 17:19:14', 1, 1, 0),
(5, 'How to detect iPhone or iPod in javascript on the website and redirect those on the different location', 1, 'if ((navigator.userAgent.indexOf(''iPhone'') != -1) || (navigator.userAgent.indexOf(''iPod'') != -1)) {\r\n	 document.location = ''http://www.xprogress.com/category-1-iphone-development/'';\r\n}', 'This piece of the code can detect iPhone or iPod Safari and will redirect those browser on the different location.', '', 'iPod, iPhone, javascript, redirection', 0, '2009-08-10 16:29:09', '2009-08-10 16:29:51', 1, 1, 0),
(6, 'How to animate UISwitch while switching from function', 1, '- (IBAction) toggleSwitchWithAnimation: (BOOL) how {\r\n	// beginning the animation\r\n	[UIView beginAnimations:nil context:NULL];\r\n	\r\n	// setting duration of the animatio\r\n	[UIView setAnimationDuration: 0.2];\r\n	\r\n	// setting new value for the switch\r\n	resSwitch.on = how;\r\n	\r\n	// commiting the animation for execution\r\n	[UIView commitAnimations];\r\n}', '<p>I you want to change state of the UISwitch with animation (looks better :)) just check this snippet.</p>\r\n<p>We will be using UISwitch, beginAnimations, setAnimationDuration and commitAnimations</p>', '', 'uiswitch, iPhone, development, animate', 1, '2009-08-12 17:31:19', '2009-08-12 17:31:19', 1, 1, 0),
(7, 'Set background image for UIView in Objective-C on iPhone', 1, '- (void)viewDidLoad {\r\n	self.view.backgroundColor = [[UIColor alloc] initWithPatternImage:[UIImage imageNamed:@"yourImageFromResources.png"]];\r\n    [super viewDidLoad];\r\n}', '<p>Example how to implement background image for UIView in your iPhone application.</p>', '', '', 1, '2009-08-14 15:31:34', '2009-08-14 15:31:34', 1, 1, 0),
(8, 'Calling a javascript function in your UIWebView from Objective-C', 1, '\r\nNSString *jsCommand = [NSString stringWithFormat:@"callSomeJavasctiptFunction(%d, %d, %d);", @"parameter1", @"parameter2", @"parameter3"];\r\n[self.webView stringByEvaluatingJavaScriptFromString:jsCommand];\r\n', 'This snippet may help you if you have some, for example SVG content, in your web view (UIWebView) and you want to call some specific javascript function from it.', '', 'UIWebView, Web View, Objective-C, JavaScript, call function', 1, '2009-08-19 12:34:37', '2009-08-19 12:36:04', 1, 1, 0),
(9, 'Show custom error messages with UIAlertView', 1, 'UIAlertView * alertView = [[UIAlertView alloc] initWithTitle: @"My Error" message: @"This is my error message." delegate: nil cancelButtonTitle: @"OK" otherButtonTitles: nil];\r\n[alertView show];\r\n[alertView release];\r\n	', 'If you want to display your own Error or Alert messages in your iPhone application, you need to use the UIAlertView. This simple snippet will show you how to do that.', 'This piece of the code can be placed into some action like UIButton has been pressed or something like that. ', 'Error message, alert message, UIAlertView', 1, '2009-08-19 16:10:22', '2009-08-19 16:12:09', 1, 1, 0),
(10, 'Example of threading in iPhone SDK (Objective-C using NSThread)', 1, '- (IBAction) startThreadButtonPressed:(UIButton *)sender {\r\n	\r\n	[NSThread detachNewThreadSelector:@selector(startTheBackgroundJob) toTarget:self withObject:nil];\r\n	\r\n}\r\n\r\n- (void)startTheBackgroundJob {\r\n	\r\n	NSAutoreleasePool *pool = [[NSAutoreleasePool alloc] init];\r\n	// wait for 3 seconds before starting the thread, you don''t have to do that. This is just an example how to stop the NSThread for some time\r\n	[NSThread sleepForTimeInterval:3];\r\n    [self performSelectorOnMainThread:@selector(makeMyProgressBarMoving) withObject:nil waitUntilDone:NO];\r\n    [pool release];\r\n	\r\n}\r\n\r\n- (void)makeMyProgressBarMoving {\r\n    \r\n	float actual = [threadProgressView progress];\r\n	if (actual < 1) {\r\n		threadProgressView.progress = actual + 0.01;\r\n		[NSTimer scheduledTimerWithTimeInterval:0.5 target:self selector:@selector(makeMyProgressBarMoving) userInfo:nil repeats:NO];\r\n	}\r\n	\r\n}', 'Example how to use create a new thread using NSThread to run some method in the background of your application.', '<a href="http://www.xprogress.com/post-36-threading-tutorial-using-nsthread-in-iphone-sdk-objective-c/" class="exitlink">Update: Full tutorial about threading with NSThread and sample application is here</a>', 'NSThread, threading, thread, iphone sdk, objective-c, performSelectorOnMainThread', 1, '2009-09-04 17:38:18', '2009-09-25 15:37:38', 1, 1, 0),
(11, 'How to enable or disable iPhone status bar in XCode', 1, '[[UIApplication sharedApplication] setStatusBarHidden:YES animated:NO];', '<p>Here you can see how to enable or disable (show/hide) the grey statusbar with the name of your operator and battery status in your iPhone application.</p>', '<p>or you can edit your plist file and set:</p>\r\n\r\n<pre>\r\n<key>UIStatusBarHidden</key>\r\n&lt;true />\r\n</pre>', 'setStatusBarHidden, UIStatusBarHidden, UIApplication, sharedApplication', 1, '2009-09-16 20:49:05', '2009-09-16 20:50:28', 1, 1, 0),
(12, 'mapkit regions', 0, 'region.center.latitude = mapView.userLocation.coordinate.latitude;\r\nregion.center.longitude = mapView.userLocation.coordinate.longitude;\r\nregion.span.latitudeDelta = 0.0039;\r\nregion.span.longitudeDelta = 0.0034;\r\nmapView.region = region;', '', '', '', 1, '2009-09-17 12:31:29', '2009-09-17 12:31:29', 1, 1, 0),
(13, 'How to read a file, line by line into the NSArray in Objective-C', 1, 'NSString *entireFileInString = [NSString stringWithContentsOfFile:filePath]; // reads file into memory as an NSString\r\nNSArray *lines = [entireFileInString componentsSeparatedByString:@"\\n"]; // each line, adjust character for line endings\r\nfor (NSString *line in lines) NSLog([NSString stringWithFormat:@"line: %@", line]);', 'This piece of the code will first, load your file into the NSString and that by using componentsSeparatedByString will parse all loaded lines into the NSArray.', 'Be careful while using large files, this can take a lot of memory.', 'componentsSeparatedByString, separate file line by line, NSArray, NSString', 1, '2009-09-17 18:18:01', '2009-09-17 18:23:11', 1, 1, 0),
(14, 'Example of using NSMutableArray and addObject in Objective-C in XCode', 1, 'NSMutableArray *myArray = [[NSMutableArray alloc] init];\r\n[myArray addObject:@"First line"];\r\n[myArray addObject:@"Second line"];\r\n[myArray addObject:@"Third line"];\r\n[myArray addObject:@"Fourth line"];\r\n[myArray addObject:@"Fifth line"];\r\n\r\n\r\nfor (NSString *line in myArray) {\r\n	// Results from NSLog are in the Console\r\n	NSLog([NSString stringWithFormat:@"line: %@", line]);\r\n}', '<p>Example of using NSMutableArray and than reading all the variables back into the NSLog as a NSString.</p>', '<p>Results from NSLog are in the Console, you can find this one by pressing "Apple key + Shift + R" or Run-&gt;Console</p>', '', 1, '2009-09-17 18:29:37', '2009-09-17 18:29:37', 1, 1, 0),
(15, 'Check the first launch of the application on iPhone (Objective-C, XCode, iPhone SDK)', 1, '// applicationDidFinishLaunching from your main app delegate\r\n- (void)applicationDidFinishLaunching:(UIApplication *)application {\r\n	// ... standard apple code\r\n    [[NSUserDefaults standardUserDefaults] registerDefaults:[NSDictionary dictionaryWithObjectsAndKeys:[NSNumber numberWithBool:YES], @"isFirstLaunch",nil]];\r\n}\r\n\r\n\r\n// to check the first launch in the code\r\nif ([[NSUserDefaults standardUserDefaults] boolForKey:@"isFirstLaunch"]) {\r\n	 // place your code here\r\n}\r\n\r\n\r\n// put this code to your applicationWillTerminate function to set "isFirstLaunch" to NO after first launch\r\n[[NSUserDefaults standardUserDefaults] setBool:NO forKey:@"isFirstLaunch"];\r\n', '<p>Really simple example how to check the first launch of the iPhone application written in Objective-C in XCode.</p>', '', '', 1, '2009-09-22 02:30:56', '2009-09-22 02:31:45', 1, 1, 0),
(16, 'sending file', 0, '- (NSData*)generateFormData:(NSDictionary*)dict\r\n{\r\n	NSString* boundary = [NSString stringWithString:@"_insert_some_boundary_here_"];\r\n	NSArray* keys = [dict allKeys];\r\n	NSMutableData* result = [[NSMutableData alloc] initWithCapacity:100];\r\n\r\n	int i;\r\n	for (i = 0; i < [keys count]; i++) \r\n	{\r\n		id value = [dict valueForKey: [keys objectAtIndex: i]];\r\n		[result appendData:[[NSString stringWithFormat:@"--%@\\n", boundary] dataUsingEncoding:NSASCIIStringEncoding]];\r\n		if ([value class] == [NSString class] || [value class] == [NSConstantString class])\r\n		{\r\n			[result appendData:[[NSString stringWithFormat:@"Content-Disposition: form-data; name=\\"%@\\"\\n\\n", [keys objectAtIndex:i]] dataUsingEncoding:NSASCIIStringEncoding]];\r\n			[result appendData:[[NSString stringWithFormat:@"%@",value] dataUsingEncoding:NSASCIIStringEncoding]];\r\n		}\r\n		else if ([value class] == [NSURL class] && [value isFileURL])\r\n		{\r\n			[result appendData:[[NSString stringWithFormat:@"Content-Disposition: form-data; name=\\"%@\\"; filename=\\"%@\\"\\n", [keys objectAtIndex:i], [[value path] lastPathComponent]] dataUsingEncoding:NSASCIIStringEncoding]];\r\n			[result appendData:[[NSString stringWithString:@"Content-Type: application/octet-stream\\n\\n"] dataUsingEncoding:NSASCIIStringEncoding]];\r\n			[result appendData:[NSData dataWithContentsOfFile:[value path]]];\r\n		}\r\n		[result appendData:[[NSString stringWithString:@"\\n"] dataUsingEncoding:NSASCIIStringEncoding]];\r\n	}\r\n	[result appendData:[[NSString stringWithFormat:@"--%@--\\n", boundary] dataUsingEncoding:NSASCIIStringEncoding]];\r\n	\r\n	return [result autorelease];\r\n}\r\n\r\n\r\nNSMutableDictionary* post_dict = [[NSMutableDictionary alloc] initWithCapacity:2];\r\n[post_dict setObject:@"test_value" forKey:@"test_key"];\r\n[post_dict setObject:[NSURL fileURLWithPath:@"/Butterfly.tif"] forKey:@"file1"];\r\nNSData* regData = [self generateFormData:post_dict];\r\n[post_dict release];\r\n\r\nNSMutableURLRequest* post = [NSMutableURLRequest requestWithURL: [NSURL URLWithString:@"http://yourdomain.com/post.php"]];\r\n[post addValue: @"multipart/form-data; boundary=_insert_some_boundary_here_" forHTTPHeaderField: @"Content-Type"];\r\n[post setHTTPMethod: @"POST"];\r\n[post setHTTPBody:regData];\r\nNSURLResponse* response;\r\nNSError* error;\r\nNSData* result = [NSURLConnection sendSynchronousRequest:post returningResponse:&response error:&error];\r\nNSLog(@"%@", [[[NSString alloc] initWithData:result encoding:NSASCIIStringEncoding] autorelease]);\r\n\r\n', '', '', '', 1, '2009-09-28 13:25:59', '2009-09-28 13:25:59', 1, 1, 0),
(17, 'How to keep the iphone screen on, and stop it from auto locking', 1, '- (void)applicationDidFinishLaunching:(UIApplication *)application {\r\n	application.idleTimerDisabled = YES;\r\n}', '<p>Very nice snippet, this allows you to keep your screen turned on as long as you need. Using the idleTimerDisabled in applicationDidFinishLaunching (UIApplication) </p>', 'Maybe will be a good idea to create some settings to disable this functionality as it can drain the users battery very quickly ... especially if you are using some WiFi or GPS in the same time.', 'idleTimerDisabled, screen turned on, UIApplication, applicationDidFinishLaunching', 1, '2009-10-13 17:08:34', '2009-10-13 17:09:34', 1, 1, 0),
(18, 'Request with custom user agent', 0, 'NSString* userAgent = @"My Cool User Agent";\r\nNSURL* url = [NSURL URLWithString:url];\r\nNSMutableURLRequest* request = [[[NSMutableURLRequest alloc] initWithURL:url] autorelease];\r\n[request setValue:userAgent forHTTPHeaderField:@"User-Agent"];	\r\nNSURLResponse* response = nil;\r\nNSError* error = nil;\r\nNSData* data = [NSURLConnection sendSynchronousRequest:request returningResponse:&response error:&error];	\r\nNSString *myData = [[NSString alloc] initWithData:data encoding:NSUTF8StringEncoding];	\r\n', '', '', '', 1, '2009-10-16 00:00:33', '2009-10-16 00:00:33', 1, 1, 0),
(19, 'explode', 0, 'NSString *bigString = @"not really that big";\r\n NSArray *words = [bigString componentsSeparatedByCharactersInSet:[NSCharacterSet whitespaceCharacterSet]];\r\n\r\n\r\n\r\nNSMutableCharacterSet *separators = [NSMutableCharacterSet punctuationCharacterSet];\r\n[separators formUnionWithCharacterSet:[NSCharacterSet whitespaceAndNewlineCharacterSet]];\r\nNSArray *words = [bigString componentsSeparatedByCharactersInSet:separators];\r\n\r\n', '', '', '', 1, '2009-10-16 00:17:43', '2009-10-16 00:17:43', 1, 1, 0),
(20, 'UIAlertView with textField', 0, 'UIAlertView *myAlert = [[UIAlertView alloc] initWithTitle:@"Add new keyword" message:@"Please type new keyword you want to get stats for" delegate:self cancelButtonTitle:@"Cancel" otherButtonTitles:@"Ok", nil];\r\n[myAlert addTextFieldWithValue:nil label:@"Keyword"];\r\n[[myAlert textField] setDelegate:self];\r\n[[myAlert textField] setTextAlignment:UITextAlignmentCenter];\r\n[[myAlert textField] becomeFirstResponder];\r\n[myAlert show];\r\n[myAlert release];\r\nmyAlert = nil;\r\n\r\n// or you can use more valid code:\r\n\r\n- (void) popupAddKeyword {\r\n	UIAlertView *alert = [[UIAlertView alloc] initWithTitle:@"Add new keyword" message:@"                                         " delegate:self cancelButtonTitle:@"Cancel" otherButtonTitles:@"Ok", nil];\r\n	CGRect frame = CGRectMake(12.0, 45.0, 260.0, 25.0);\r\n	if(!emailField) {\r\n		emailField = [[UITextField alloc] initWithFrame:frame];		\r\n		emailField.borderStyle = UITextBorderStyleBezel;		\r\n		emailField.textColor = [UIColor blackColor];		\r\n		emailField.textAlignment = UITextAlignmentCenter;		\r\n		emailField.font = [UIFont systemFontOfSize:14.0];		\r\n		emailField.placeholder = @"Keyword";		\r\n		emailField.backgroundColor = [UIColor whiteColor];		\r\n		emailField.autocorrectionType = UITextAutocorrectionTypeNo; // no auto correction support		\r\n		emailField.keyboardType = UIKeyboardTypeEmailAddress; // use the default type input method (entire keyboard)		\r\n		emailField.returnKeyType = UIReturnKeyDone;		\r\n		emailField.delegate = self;		\r\n		emailField.clearButtonMode = UITextFieldViewModeWhileEditing; // has a clear ''x'' button to the right		\r\n	}	\r\n	[alert addSubview:emailField];	\r\n	[alert show];\r\n	[alert release];\r\n}', '', '', '', 1, '2009-10-19 19:13:18', '2009-10-19 19:13:18', 1, 1, 0),
(21, 'Declaring classes from string', 0, 'Class someClass = NSClassFromString(@"MyClass");\r\nid instance = [[someClass alloc] init];\r\n[instance someFunction];\r\n', '', '', '', 1, '2009-11-04 17:16:37', '2009-11-04 17:16:45', 1, 1, 0),
(22, 'Escape single quotes in strings for SQLite3 database', 1, '+ (NSString *) escapeString:(NSString *)string {\r\n	NSRange range = NSMakeRange(0, [string length]);\r\n	return [string stringByReplacingOccurrencesOfString:@"''" withString:@"''''" options:NSCaseInsensitiveSearch range:range];\r\n}\r\n', '<p>This function will escape the NSString for SQLite3 database. You can use this function to escape or change any other character.</p>', '', '', 1, '2009-11-05 12:38:40', '2009-11-05 12:38:40', 1, 1, 0),
(23, 'Validating email address field in iPhone SDK / XCode ', 1, '+ (BOOL)validateEmail:(NSString *)email {\r\n	NSString *emailRegex = @"[A-Z0-9a-z._%+-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,4}"; \r\n    NSPredicate *emailTest = [NSPredicate predicateWithFormat:@"SELF MATCHES %@", emailRegex]; \r\n    return [emailTest evaluateWithObject:email];\r\n}', '<p>Example of validation of e-mail address field in Cocoa and on iPhone in XCode using regular expression without need of using RegexKitLite</p>', '', 'RegexKitLite, iPhone SDK, XCode, email address validation', 1, '2010-03-10 18:27:11', '2010-03-10 18:27:11', 0, 1, 0),
(24, 'How to run NSTimer in NSThread', 0, '-(void)someMethod{\r\n	[NSThread detachNewThreadSelector:@selector(refreshThread) toTarget:self withObject:nil];\r\n}\r\n\r\n-(void)refreshThread{\r\n	NSAutoreleasePool *pool=[[NSAutoreleasePool alloc] init];\r\n	[NSTimer scheduledTimerWithTimeInterval:0.05 target:self selector:@selector(refresh) userInfo:nil repeats:YES];\r\n	[[NSRunLoop currentRunLoop] run];\r\n	[pool release];\r\n}', 'Example of setting up NSTimer in new thread by startin runloop of the current thread.', 'Example of setting up NSTimer in new thread by startin runloop of the current thread.', 'NSThread,NSTimer,NSRunLoop,timers,threads,setting runloop', 1, '2010-03-11 08:43:31', '2010-03-13 21:21:58', 28, 0, 0),
(25, 'How to remove first characters from string in iPhone SDK', 1, 'NSString *str = @"Quick brown fox jumped over lazy dog";\r\nNSString *newStr = [str substringWithRange:NSMakeRange(6, [str length] - 6)];\r\n\r\n// or you can use\r\n\r\nNSString *str = @"Quick brown fox jumped over lazy dog";\r\nNSString *newStr = [str substringFromIndex:6];\r\n\r\n// in both cases you''ll get "brown fox jumped over lazy dog"\r\nNSLog(@"New string: %@", newStr);\r\n\r\n\r\n', 'Removing first  ore more characters from NSString in XCode (Objective-C; iPhone SDK)', '', 'Objective-C, iPhone SDK, NSString', 1, '2010-03-11 15:05:32', '2010-03-11 15:06:20', 0, 1, 0),
(26, 'UIView programmatically with rounded corners', 1, '// don''t forget to import quartz core\r\n#import &lt;QuartzCore/QuartzCore.h&gt;\r\n\r\n// setting up size and position of the UIView\r\nint leftBorder = 20;\r\nint topBorder = 35;\r\nint width = 150;\r\nint height = 160;\r\n\r\n// creating new view\r\nUIView *myView = [[UIView alloc] initWithFrame:CGRectMake(leftBorder, topBorder, width, height)];\r\n\r\n// setting up rounded corners\r\nmyView.layer.cornerRadius = 10;\r\n\r\n// setting color for our view\r\nmyView.backgroundColor = [UIColor redColor];\r\n\r\n// adding our new view on the\r\n[self.view addSubview:myView];\r\n\r\n', '<p>Short snippet that explains how to create new UIView programmatically, and than setu rounded corners, background color and than add this view to your main view on the screen</p>', '<p>Be aware that this functionality works with iPhone SDK 3.0 and newer, and you have to have the QuartzCore framework imported in your project :)</p>\r\n', '', 1, '2010-04-15 22:09:49', '2010-04-16 11:33:37', 0, 1, 0),
(27, 'Sending email message from the app using MFMailComposeViewController in MessageUI on iPhone', 1, '// Header file\r\n\r\n// importing the MessageUI framework\r\n#import &lt;MessageUI/MessageUI.h>\r\n\r\n// adding the delegate functionality to the class (&lt;MFMailComposeViewControllerDelegate>)\r\n@interface TutorialProjectViewController : UIViewController &lt;MFMailComposeViewControllerDelegate> {\r\n\r\n}\r\n\r\n- (IBAction)pressTheMailButtonDudeFunction:(id)sender;\r\n\r\n\r\n\r\n// Implementation file\r\n\r\n- (IBAction)pressTheMailButtonDudeFunction:(id)sender {\r\n	\r\n	// allocatind new message composer window\r\n	MFMailComposeViewController *mc = [[MFMailComposeViewController alloc] init];\r\n	\r\n	// setting a delegate method to "self"\r\n	mc.mailComposeDelegate = self;\r\n	\r\n	// pre-populating the message subject\r\n	[mc setSubject:@"Send me a message"];\r\n	\r\n	// adding content of the message as a plain text\r\n	[mc setMessageBody:@"Send me a message is you like this tutorial :)" isHTML:NO];\r\n	\r\n	// adding content of the message as an HTML\r\n	[mc setMessageBody:@"&lt;p>Send me a message is you like this tutorial :)&lt;p>" isHTML:YES];\r\n	\r\n	// adding recipients\r\n	[mc setToRecipients:[NSArray arrayWithObjects:@"Fuerte &lt;info@fuerte.cz>", @"info@xprogress.com", nil]];\r\n	\r\n	// adding recipients for a send copy to (arrayWithObject or arrayWithObjects)\r\n	[mc setCcRecipients:[NSArray arrayWithObject:@"test@example.com"]];\r\n	\r\n	// adding hidden recipients\r\n	[mc setBccRecipients:[NSArray arrayWithObject:@"test@example.com"]];\r\n	\r\n	// adding image attachment\r\n	// getting path for the image we have in the tutorial project\r\n	NSString *path = [[NSBundle mainBundle] pathForResource:@"Extra_Xcode_100x100" ofType:@"png"];\r\n	\r\n	// loading content of the image into NSData\r\n	NSData *imageData = [NSData dataWithContentsOfFile:path];\r\n	\r\n	// adding the attachment to he message\r\n	[mc addAttachmentData:imageData mimeType:@"image/png" fileName:@"My tutorial image"];\r\n	\r\n	// setting different than the default transition for the modal view controller\r\n	[mc setModalTransitionStyle:UIModalTransitionStyleCrossDissolve];\r\n	\r\n	/*\r\n	 Modal view controllers transitions:\r\n	 \r\n	 UIModalTransitionStyleCoverVertical => pops up from the bottom, default transition\r\n	 UIModalTransitionStyleCrossDissolve => fade on the screen\r\n	 UIModalTransitionStyleFlipHorizontal => page flip\r\n	 */\r\n	\r\n	// displaying our modal view controller on the screen (of course animated has to be set on YES if you want to see any transition)\r\n	[self presentModalViewController:mc animated:YES];\r\n	\r\n	// releasing the controller\r\n	[mc release];\r\n}\r\n\r\n// delegate function callback\r\n- (void)mailComposeController:(MFMailComposeViewController *)controller didFinishWithResult:(MFMailComposeResult)result error:(NSError *)error {\r\n	// switchng the result\r\n	switch (result) {\r\n		case MFMailComposeResultCancelled:\r\n			NSLog(@"Mail send canceled.");\r\n			/*\r\n			 Execute your code for canceled event here ...\r\n			 */\r\n			break;\r\n		case MFMailComposeResultSaved:\r\n			NSLog(@"Mail saved.");\r\n			/*\r\n			 Execute your code for email saved event here ...\r\n			 */\r\n			break;\r\n		case MFMailComposeResultSent:\r\n			NSLog(@"Mail sent.");\r\n			/*\r\n			 Execute your code for email sent event here ...\r\n			 */\r\n			break;\r\n		case MFMailComposeResultFailed:\r\n			NSLog(@"Mail send error: %@.", [error localizedDescription]);\r\n			/*\r\n			 Execute your code for email send failed event here ...\r\n			 */\r\n			break;\r\n		default:\r\n			break;\r\n	}\r\n	// hide the modal view controller\r\n	[self dismissModalViewControllerAnimated:YES];\r\n}', '<p>Just a short version of our tutorial "How to send mail from an iPhone app"</p>', '<p>Don''t forget to import the MessageUI framework into your project :)</p>', 'MessageUI, MFMailComposeViewController, iPhone SDK, Objective-C', 1, '2010-04-16 13:53:08', '2010-04-16 14:29:46', 0, 1, 0);
