(564, 3, 1, '<?xml version="1.0" encoding="utf-8"?>\n<response>\n	<a key="name">PREMKUMAR</a>\n	<a key="email">everythingmaker@gmail.com</a>\n	<a key="url">CHECKBOX Tutorial for begineers</a>\n	<a key="message">#import &lt;UIKit/UIKit.h&gt;\r\n\r\n@interface RootViewController : UITableViewController {\r\n  \r\n	NSMutableArray *ArrayList;\r\n	NSInteger selectedValue;\r\n	NSString *selectedText;\r\n  \r\n}\r\n@property(nonatomic,retain)NSMutableArray *ArrayList;\r\n@property(nonatomic,retain)NSString *selectedText;\r\n@end\r\n\r\n\r\n.m:\r\n\r\n#import &quot;RootViewController.h&quot;\r\n\r\n\r\n@implementation RootViewController\r\n@synthesize ArrayList;\r\n@synthesize selectedText;\r\n\r\n#pragma mark -\r\n#pragma mark View lifecycle\r\n\r\n\r\n- (void)viewDidLoad {\r\n    [super viewDidLoad];\r\n	\r\n	ArrayList=[[NSMutableArray alloc]initWithObjects:@&quot;Male&quot;,@&quot;Female&quot;,@&quot;TransGendered&quot;,nil];\r\n	NSLog(@&quot;%@&quot;,[ArrayList objectAtIndex:0]);\r\n    selectedText=@&quot;&quot;;\r\n	selectedValue=-1;\r\n    // Uncomment the following line to display an Edit button in the navigation bar for this view controller.\r\n    // self.navigationItem.rightBarButtonItem = self.editButtonItem;\r\n	\r\n	UIBarButtonItem *saveButtonItem = [[UIBarButtonItem alloc] initWithTitle:@&quot;Save&quot; style:UIBarButtonItemStyleDone target:self action:@selector(SelectItem)];\r\n    self.navigationItem.rightBarButtonItem = saveButtonItem;\r\n    [saveButtonItem release];\r\n\r\n	\r\n}\r\n\r\n-(void)SelectItem{\r\n	\r\n	NSLog(@&quot;ITEM NAME:%@&quot;,selectedText);\r\n	NSLog(@&quot;ITEM ID:%d&quot;,selectedValue);\r\n}\r\n/*\r\n- (void)viewWillAppear:(BOOL)animated {\r\n    [super viewWillAppear:animated];\r\n}\r\n*/\r\n/*\r\n- (void)viewDidAppear:(BOOL)animated {\r\n    [super viewDidAppear:animated];\r\n}\r\n*/\r\n/*\r\n- (void)viewWillDisappear:(BOOL)animated {\r\n	[super viewWillDisappear:animated];\r\n}\r\n*/\r\n/*\r\n- (void)viewDidDisappear:(BOOL)animated {\r\n	[super viewDidDisappear:animated];\r\n}\r\n*/\r\n\r\n/*\r\n // Override to allow orientations other than the default portrait orientation.\r\n- (BOOL)shouldAutorotateToInterfaceOrientation:(UIInterfaceOrientation)interfaceOrientation {\r\n	// Return YES for supported orientations.\r\n	return (interfaceOrientation == UIInterfaceOrientationPortrait);\r\n}\r\n */\r\n\r\n\r\n#pragma mark -\r\n#pragma mark Table view data source\r\n\r\n// Customize the number of sections in the table view.\r\n- (NSInteger)numberOfSectionsInTableView:(UITableView *)tableView {\r\n    return 1;\r\n}\r\n\r\n\r\n// Customize the number of rows in the table view.\r\n- (NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section {\r\n    return 3;\r\n}\r\n\r\n\r\n// Customize the appearance of table view cells.\r\n- (UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath {\r\n    \r\n    static NSString *CellIdentifier = @&quot;Cell&quot;;\r\n    \r\n    UITableViewCell *cell = [tableView dequeueReusableCellWithIdentifier:CellIdentifier];\r\n    if (cell == nil) {\r\n        cell = [[[UITableViewCell alloc] initWithStyle:UITableViewCellStyleDefault reuseIdentifier:CellIdentifier] autorelease];\r\n    }\r\n    \r\n	if (selectedValue==indexPath.row) {\r\n		cell.accessoryType=UITableViewCellAccessoryCheckmark;\r\n		selectedText=(NSString *)[ArrayList objectAtIndex:indexPath.row];\r\n	}\r\n    cell.textLabel.text= (NSString *)[ArrayList objectAtIndex:indexPath.row];\r\n    return cell;\r\n}\r\n\r\n\r\n/*\r\n// Override to support conditional editing of the table view.\r\n- (BOOL)tableView:(UITableView *)tableView canEditRowAtIndexPath:(NSIndexPath *)indexPath {\r\n    // Return NO if you do not want the specified item to be editable.\r\n    return YES;\r\n}\r\n*/\r\n\r\n\r\n/*\r\n// Override to support editing the table view.\r\n- (void)tableView:(UITableView *)tableView commitEditingStyle:(UITableViewCellEditingStyle)editingStyle forRowAtIndexPath:(NSIndexPath *)indexPath {\r\n    \r\n    if (editingStyle == UITableViewCellEditingStyleDelete) {\r\n        // Delete the row from the data source.\r\n        [tableView deleteRowsAtIndexPaths:[NSArray arrayWithObject:indexPath] withRowAnimation:UITableViewRowAnimationFade];\r\n    }   \r\n    else if (editingStyle == UITableViewCellEditingStyleInsert) {\r\n        // Create a new instance of the appropriate class, insert it into the array, and add a new row to the table view.\r\n    }   \r\n}\r\n*/\r\n\r\n\r\n/*\r\n// Override to support rearranging the table view.\r\n- (void)tableView:(UITableView *)tableView moveRowAtIndexPath:(NSIndexPath *)fromIndexPath toIndexPath:(NSIndexPath *)toIndexPath {\r\n}\r\n*/\r\n\r\n\r\n/*\r\n// Override to support conditional rearranging of the table view.\r\n- (BOOL)tableView:(UITableView *)tableView canMoveRowAtIndexPath:(NSIndexPath *)indexPath {\r\n    // Return NO if you do not want the item to be re-orderable.\r\n    return YES;\r\n}\r\n*/\r\n\r\n\r\n#pragma mark -\r\n#pragma mark Table view delegate\r\n\r\n- (void)tableView:(UITableView *)tableView didSelectRowAtIndexPath:(NSIndexPath *)indexPath {\r\n    \r\n	UITableViewCell *cell=nil;\r\n	if(selectedValue &gt;= 0)\r\n	{\r\n		cell= [tableView cellForRowAtIndexPath:[NSIndexPath indexPathForRow:selectedValue inSection:0]];\r\n		cell.accessoryType = UITableViewCellAccessoryNone;\r\n	}\r\n	\r\n	cell= [tableView cellForRowAtIndexPath:indexPath];\r\n	cell.accessoryType = UITableViewCellAccessoryCheckmark;\r\n	\r\n	[tableView deselectRowAtIndexPath:indexPath animated:YES];\r\n	selectedValue = indexPath.row;\r\n	NSLog(@&quot;%d&quot;,selectedValue);\r\n	selectedText = (NSString *)[ArrayList objectAtIndex:indexPath.row];\r\n	NSLog(@&quot;%@&quot;,selectedText);\r\n}\r\n\r\n\r\n#pragma mark -\r\n#pragma mark Memory management\r\n\r\n- (void)didReceiveMemoryWarning {\r\n    // Releases the view if it doesn''t have a superview.\r\n    [super didReceiveMemoryWarning];\r\n    \r\n    // Relinquish ownership any cached data, images, etc that aren''t in use.\r\n}\r\n\r\n- (void)viewDidUnload {\r\n    // Relinquish ownership of anything that can be recreated in viewDidLoad or on demand.\r\n    // For example: self.myOutlet = nil;\r\n}\r\n\r\n\r\n- (void)dealloc {\r\n    [super dealloc];\r\n}\r\n\r\n\r\n@end\r\n</a>\n</response>', '2011-10-11 09:56:17', 0, NULL),
